#define MAX_ITER 100
#define BOUNDARY 2.
#define PI 3.141592653589

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

vec2 rotate(vec2 v, float a)
{
	float s = sin(a);
	float c = cos(a);
	mat2 m = mat2(c, -s, s, c);
	return m * v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime + 4.;
    vec2 uv = fragCoord / iResolution.xy * 2. - vec2(1., 1.);

    int i = 0;
    for(; i < MAX_ITER; i++)
    {
        if(length(uv) > BOUNDARY)
            break;
        uv = rotate(uv, time / 10.);
        uv *= 1.03;
        uv += vec2(cos(time / 5.), sin(time / 5.));
        // uv += rotate(uv, PI / 2.);
    }
    if(length(uv) < BOUNDARY)
    {
        fragColor = vec4(0);
        return;
    }
    float h = atan(uv.y, uv.x) / (2. * PI) + 0.5;
    float s = float(i) / float(MAX_ITER);
    float v = s;
    s *= 1.5;
    fragColor = vec4(hsv2rgb(vec3(h, s, v)), 1);
}
