float circle(in vec2 _st, in float _radius){

    vec2 l = _st-.5;
    float pix = 10./iResolution.y;
    return smoothstep(_radius+pix,
                      _radius-pix,
                      length(l)*1.5);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 st = fragCoord/iResolution.y;
    vec3 color = vec3(0.0);

    float u_time=iTime;
    vec2 nst =st* 10.0;      // 
    float movex=step(1.,mod(u_time,2.))*mod(u_time,1.);
    float movey=step(1.,mod(u_time+1.,2.))*mod(u_time+1.,1.);
    vec2 xbar=step(vec2(.1),mod(st,0.2));
    nst.y+=movex*xbar.x;
    nst.x+=movey*xbar.y;

    nst = fract(nst); // Wrap around 1.0


    float tem=distance(st,vec2(sin(st.x)));
    //color = vec3(nst.x,nst.y,0);
    color = vec3(circle(nst,0.5));



    fragColor = vec4(color,1.0);
}
