// Testing this shader: https://www.shadertoy.com/view/7tBfD3


// smoothstep and its inverse
// https://iquilezles.org/articles/ismoothstep/
float csmoothstep( float x ) { return x*x*(3.0-2.0*x); }
float inverse_csmoothstep( float x) { return 0.5-sin(asin(1.0-2.0*x)/3.0); }

// cantorization of smoothstep
float cantor( float x )
{
    float y = 0.0;
    float sc = 0.5;
    float bi = 0.0;
    float xa = 0.0;
    float xb = 1.0;
    for( int i=0; i<9; i++ )
    {
        // choose subdivision intervals
        float ya = csmoothstep(xa);
        float yb = csmoothstep(xb);
        float wa = inverse_csmoothstep(ya+(yb-ya)/3.0);
        float wb = inverse_csmoothstep(yb-(yb-ya)/3.0);
        // recurse
             if( x<wa ) { bi+=0.0*sc; y=bi+sc*(x-xa)/(wa-xa); xb=wa; }
        else if( x>wb ) { bi+=1.0*sc; y=bi+sc*(x-wb)/(xb-wb); xa=wb; }
        else            { bi+=0.0*sc; y=bi+sc;                break; }
        sc *= 0.5;
    }
    return y;
}

// render
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // pixel coordinates
    vec2  p = fragCoord/iResolution.y;
    float px = 1.0/iResolution.y;
    float mx = iResolution.x*px;

    // size of disks
    const float ra = 0.15;

    // movement (repeat every 4 seconds)
    float time = 1.0-abs(2.0*fract(iTime/4.0)-1.0);
    float x = (p.y>0.5) ? cantor(time) : 
                          csmoothstep(time);
    // center
    vec2 c = vec2(ra+(mx-ra*2.0)*x, (p.y>0.5)?0.75:0.25);

    // disks
    float d = length(p-c)-ra;

    // color
    vec3 col = vec3( 0.2+0.8*smoothstep(px,-px,d) );

    fragColor = vec4( col, 1.0);
}
