const float MAX_TRACE_DISTANCE = 10.0;           // max trace distance
const float INTERSECTION_PRECISION = .0001;        // precision of the intersection
const int NUM_OF_TRACE_STEPS = 40;

vec4 spherePos[100];



// Taken From
//https://www.shadertoy.com/view/MsX3RH

vec3 nmap(vec2 t, sampler2D tx, float str)
{
	float d=1.0/1024.0;

	float xy=texture(tx,t).x;
	float x2=texture(tx,t+vec2(d,0)).x;
	float y2=texture(tx,t+vec2(0,d)).x;

	float s=(1.0-str)*1.2;
	s*=s;
	s*=s;

	return normalize(vec3(x2-xy,y2-xy,s/8.0));///2.0+0.5;
}


// Taken from https://www.shadertoy.com/view/4ts3z2
float tri(in float x){return abs(fract(x)-.5);}
vec3 tri3(in vec3 p){return vec3( tri(p.z+tri(p.y*1.)), tri(p.z+tri(p.x*1.)), tri(p.y+tri(p.x*1.)));}


// Taken from https://www.shadertoy.com/view/4ts3z2
float triNoise3D(in vec3 p, in float spd)
{
    float z=1.4;
	float rz = 0.;
    vec3 bp = p;
	for (float i=0.; i<=3.; i++ )
	{
        vec3 dg = tri3(bp*2.);
        p += (dg+iTime*.1*spd);

        bp *= 1.8;
		z *= 1.5;
		p *= 1.2;
        //p.xz*= m2;

        rz+= (tri(p.z+tri(p.x+tri(p.y))))/z;
        bp += 0.14;
	}
	return rz;
}


vec4 texCube( sampler2D sam, in vec3 p, in vec3 n, in float k )
{
	vec4 x = texture( sam, p.yz );
	vec4 y = texture( sam, p.zx );
	vec4 z = texture( sam, p.xy );
    vec3 w = pow( abs(n), vec3(k) );
	return (x*w.x + y*w.y + z*w.z) / (w.x+w.y+w.z);
}

vec3 hsv(float h, float s, float v)
{
  return mix( vec3( 1.0 ), clamp( ( abs( fract(
    h + vec3( 3.0, 2.0, 1.0 ) / 3.0 ) * 6.0 - 3.0 ) - 1.0 ), 0.0, 1.0 ), s ) * v;
}

float udRoundBox( vec3 p, vec3 b, float r )
{
  return length(max(abs(p)-b,0.0))-r;
}

float sdPlane( vec3 p )
{

    float f = sin( p.z * 5. ) * sin( p.x * 5. );
    //f = 5. * smoothstep( abs(f) , 0.4 , 0.8 );
	return p.y - (abs( f) * .3)/ max( 1. , pow( length( p ), 1.));

}



float sdSphere( vec3 p, float s , float offset)
{


    float d1 = length(p) - .1*smoothstep(0.0,1.0,iTime);;
    d1 *= .8;	

   // vec3 nP = vec3( x , y , z );
    return d1 -s;
}

float sdCone( in vec3 p, in vec3 c )
{
    vec2 q = vec2( length(p.xz), p.y );

    float d1 = -p.y-c.z;
    float d2 = max( dot(q,c.xy), p.y);
    return length(max(vec2(d1,d2),0.0)) + min(max(d1,d2), 0.);

}

//----
// Camera Stuffs
//----
mat3 calcLookAtMatrix( in vec3 ro, in vec3 ta, in float roll )
{
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(sin(roll),cos(roll),0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
    return mat3( uu, vv, ww );
}

void doCamera( out vec3 camPos, out vec3 camTar, in float time, in float mouseX )
{
    float an = 0.3 + 10.0*mouseX;
	camPos = vec3(3.5*sin(an),1.0,3.5*cos(an));
    camTar = vec3(0.0,0.0,0.0);
}




// checks to see which intersection is closer
// and makes the y of the vec2 be the proper id
vec2 opU( vec2 d1, vec2 d2 ){

	return (d1.x<d2.x) ? d1 : d2;

}

// exponential smooth min (k = 32);
float smin( float a, float b, float k )
{
    float res = exp( -k*a ) + exp( -k*b );
    return -log( res )/k;
}

float opBlend( vec2 d1, vec2 d2 )
{

    return smin( d1.x , d2.x , 8.);
}
//--------------------------------
// Modelling 
//--------------------------------
vec2 map( vec3 pos ){  

   	vec2 res = vec2( 10000. , 0.);// vec2( sdPlane( pos - vec3( 0. , -1. , 0. )), 0.0 );

    for( int i = 0; i < 20; i++ ){

        vec2 res2 = vec2( sdSphere( (pos - spherePos[i].xyz) , spherePos[i].w  , float( i )) , float(i) + 1.);
        //vec2 res2 = vec2( udRoundBox( (pos - spherePos[i].xyz) , vec3( spherePos[i].w  ) ,spherePos[i].w * .2 ) , float(i) + 1.);
   		res.x = opBlend( res ,  res2 );

   	}

   	return res;

}



vec2 calcIntersection( in vec3 ro, in vec3 rd ){


    float h =  INTERSECTION_PRECISION*2.0;
    float t = 0.0;
	float res = -1.0;
    float id = -1.;

    for( int i=0; i< NUM_OF_TRACE_STEPS ; i++ ){

        if( h < INTERSECTION_PRECISION || t > MAX_TRACE_DISTANCE ) break;
	   	vec2 m = map( ro+rd*t );
        h = m.x;
        t += h;
        id = m.y;

    }

    if( t < MAX_TRACE_DISTANCE ) res = t;
    if( t > MAX_TRACE_DISTANCE ) id =-1.0;

    return vec2( res , id );

}

// Calculates the normal by taking a very small distance,
// remapping the function, and getting normal for that
vec3 calcNormal( in vec3 pos ){

	vec3 eps = vec3( 0.001, 0.0, 0.0 );
	vec3 nor = vec3(
	    map(pos+eps.xyy).x - map(pos-eps.xyy).x,
	    map(pos+eps.yxy).x - map(pos-eps.yxy).x,
	    map(pos+eps.yyx).x - map(pos-eps.yyx).x );
	return normalize(nor);
}


float softshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
	float res = 1.0;
    float t = mint;
    for( int i=0; i<50; i++ )
    {
		float h = map( ro + rd*t ).x;
        res = min( res, 20.*h/t );
        t += clamp( h, 0.02, 0.10 );
        if( h<0.001 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );

}


float calcAO( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float hr = 0.01 + 0.612*float(i)/4.0;
        vec3 aopos =  nor * hr + pos;
        float dd = map( aopos ).x;
        occ += -(dd-hr)*sca;
        sca *= 0.5;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 );    
}


#define STEPS 5
vec3 fogCube( vec3 ro , vec3 rd , vec3 n ){

    float lum = 1.;

    vec3 col = vec3( 0. );
    for( int i = 0; i < STEPS; i++ ){
        vec3 p = ro + rd * .3  * float( i );

        lum += pow(triNoise3D( p * .3, float( i ) / float( STEPS )),.3);// + sin( p.y * 3. ) + sin( p.z * 5.);

       	col += hsv( lum * 4. + sin( iTime * .1 ) , 1. , 1. ) / lum;
    }

    return col * 2.5/float(STEPS);


}




void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    vec3 lightPos = vec3( 2. , 5. , 1. );

    for( int i =0; i < 20; i++ ){

        float x = 1. * cos(iTime *.13 * (float( i )+2.));
        float y = 1. * sin(iTime * .075 * (float( i )+4.));
        float z = 1. * sin(iTime * .1 * (float( i )+3.3));
        float r = .1 * ( sin( iTime * .1  *( float( i) +1.))+2.);

        spherePos[i] = vec4( x , y , z , r  );




    	//spherePos[i] = vec4( x * 10. , 0. , 0. , .02  );

    }





    vec2 p = (-iResolution.xy + 2.0*fragCoord.xy)/iResolution.y;
    vec2 m = iMouse.xy/iResolution.xy;

    //-----------------------------------------------------
    // camera
    //-----------------------------------------------------

    // camera movement
    vec3 ro, ta;
    doCamera( ro, ta, iTime, m.x );

    // camera matrix
    mat3 camMat = calcLookAtMatrix( ro, ta, 0.0 );  // 0.0 is the camera roll

	// create view ray
	vec3 rd = normalize( camMat * vec3(p.xy,2.0) ); // 2.0 is the lens length

    vec2 res = calcIntersection( ro , rd  );


    vec3 col = vec3( 0. );//vec3( .8 , .8 , .8 ); 

    // If we have hit something lets get real!
    if( res.y > -.5 ){

    	vec3 pos = ro + rd * res.x;
    	vec3 nor = calcNormal( pos );

        // from
       // https://www.shadertoy.com/view/MsX3RH
        //triplanar blend vector
		vec3 tpn=normalize(max(vec3(0.0),(abs(nor.xyz)-vec3(0.2))*7.0))*0.5;
        //normal mapping
        float nms=.59;
        vec3 tuv=pos*vec3(1.0 , 1. , 1.);	//texture coordinates

        vec3 nmx=nmap(tuv.yz,iChannel0,nms)+nmap(-tuv.yz,iChannel0,nms);
        vec3 nmy=nmap(tuv.xz,iChannel0,nms)+nmap(-tuv.xz,iChannel0,nms);
        vec3 nmz=nmap(tuv.xy,iChannel0,nms)+nmap(-tuv.xy,iChannel0,nms);

        vec3 nn=normalize(nmx*tpn.x+nmy*tpn.y+nmz*tpn.z);
        nor = normalize(nn + nor);


        //vec3 pat = texCube( iChannel0, 0.5*pos, nor, 10.0 ).xyz;
       // pat = normalize( pat );

        float AO = calcAO( pos , nor );

        float match = -dot( nor , rd );

        vec3 lightDir = normalize( pos - lightPos );
        vec3 refl = reflect( lightDir , nor );

        float reflMatch = dot( rd , refl );


        vec3 iriCol = fogCube( pos , rd , nor );




        col = iriCol * .5 + iriCol * pow( max( 0. , -reflMatch ) , 10. ) ;

       // float c = pow(( 1.-AO) , 5.)*  10.;
        float c = 0.;
        c += pow((1.- match),4.) * 1.;
        c += pow(( 1.-AO) , 5.)*  2.;
        col -= vec3( c );// * .4 * ( nor * .5 + .5 );


        //col = nor;
       /* if( res.y < .5 ){

            float f = sin( pos.z * 5. ) * sin( pos.x * 5. );
            //col *= 5. * smoothstep( abs(f) , 0.4 , 0.8 );
            col *= abs( f );
            col /= pow( length( pos ), 4.);
        }*/
    }
    // apply gamma correction
    //col = pow( col, vec3(0.4545) );

    fragColor = vec4( col , 1. );

}
