// pattern test + wobble = funky gunky

#define PI 3.1415926

float mysmooth(float k)
{
    //k = step(k,0.0);
    k = 1.0-smoothstep(0.0,0.02,k);
    return k;
}

float pat(vec2 uv,float p,float q,float t)
{
    float z = cos(q * PI * uv.x) * cos(p * PI * uv.y) + cos(q * PI * uv.y) * cos(p * PI * uv.x);
    z += sin(t*0.75 + uv.x+uv.y * 101.1)*0.035;	// +wobble
    float dist=abs(z);

    float mid = 0.5;
    if (iMouse.z>0.5)
        mid = 0.75;

    return 1.0-mysmooth(dist-mid);
}



vec2 smoothRot(vec2 p,float s,float m,float c,float d){
  s*=0.5;
  float k=length(p);
  float x=asin(sin(atan(p.x,p.y)*s)*(1.0-m))*k;
  float ds=k*s;
  float y=mix(ds,2.0*ds-sqrt(x*x+ds*ds),c);
  return vec2(x/s,y/s-d);
}

mat2 rotate(float a)
{
	float c = cos(a);
	float s = sin(a);
	return mat2(c, s, -s, c);
}    

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   	vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;
    float md = length(uv);
    float wo = (0.5+sin(iTime*0.375+10.0*uv.x+10.0*uv.y)*0.5)*0.05;
    float shape = mysmooth(length(uv)-(0.35+wo));
    uv*=rotate(iTime*0.1+md*0.4);
    float t = iTime*0.25;
    float cen = sin(t*0.05)*0.5;
    uv = smoothRot(uv, 4.0, 0.1, 4.0, cen);
    vec2 pq = vec2(0.0);
    pq.x = 4.0+sin(t*0.42)*2.0;
    pq.y = 3.0+sin(t*0.61)*1.7;
    float sca = 4.0+sin(t*0.1)*1.5;
    float d = 0.5+pat(uv, pq.x,pq.y,t);		// layer1
    d *= 1.+pat(uv, pq.y*sca, pq.x*sca,t);
    d*=shape;
    vec3 c1 = vec3(0.3,0.45,1.);
    vec3 c2 = vec3(0.9,0.55,0.45);
    c1 = mix(c1,c2,0.5+sin(sin(md*2.0)+iTime)*0.5);
    fragColor = vec4(c1*d,1.0);
}
