// Fork of "4-Substance" by wyatt. https://www.shadertoy.com/view/3lffzM
// 2020-08-01 02:57:11

Main {
    vec4
        n = A(U+vec2(0,1)),
        e = A(U+vec2(1,0)),
        s = A(U-vec2(0,1)),
        w = A(U-vec2(1,0));
    vec3 norm = 
        normalize(vec3(e.z*e.w-w.z*w.w,n.z*n.w-s.z*s.w,10)),
        ref = reflect(vec3(0,0,-1),norm);

	vec4 a = A(U);
    Q = 2.*a.w*vec4(.6,.6,1,1)+a.z*vec4(0,0,0,0);
	Q *= 1.+0.5*norm.x;
    if (U.x < 0.2*R.x&&U.y < 0.5*R.y) Q = vec4(0.5);
    if (U.x < 0.4*R.x&&U.y < 0.3*R.y) Q = vec4(0.5);
}

Main {
    if (iFrame%2<1) {//Reintegration
        Q = vec4(0);
        for (int x = -h; x <= h; x++)
        for (int y = -h; y <= h; y++)
        {
            vec2 u = vec2(x,y);
            vec4 a = A(U+u);
            vec2 w1 = clamp(U+u+a.xy-0.5*r,U - 0.5,U + 0.5),
                 w2 = clamp(U+u+a.xy+0.5*r,U - 0.5,U + 0.5);
            float m = (w2.x-w1.x)*(w2.y-w1.y)/(r*r);
            Q.xyz += m*a.w*a.xyz;
            Q.w += m*a.w;
        }
        if (Q.w>0.)
            Q.xyz/=Q.w;
        //Boundaries
        if (iFrame < 1) 
            Q = vec4(0,0,.5,0);

        if (length(U-vec2(.02,.9)*R)<.025*R.y)Q.xw = vec2(.5);
        if (iMouse.z>0.&&length(U-iMouse.xy)<20.) Q.yw = vec2(-1.5,.5);
        if (U.x<1.||U.y<1.) Q.xy *= 0.;
        if (U.x < 0.2*R.x&&U.y < 0.5*R.y) Q.xy *= 0.,Q.z=10.;
        if (U.x < 0.4*R.x&&U.y < 0.3*R.y) Q.xy *= 0.,Q.z=10.;
        if (R.x-U.x<1.||R.y-U.y<1.)Q.zw *=0.;
    } else {//Simulation
    	Q = A(U);vec4 q = Q;
    for (int x = -k; x<=k; x++)
	for (int y = -k; y<=k; y++)
    //if (abs(x)!=abs(y))
    if (x!=0 || y!=0)
    {
        vec2 u = vec2(x,y);
        vec4 a = A(U+u);
        u = u/dot(u,u);
        Q.xy -= q.w*0.25*a.w*(a.w*a.z-1.)*u;//force               
    	Q.z -= q.w*0.25*a.w*(dot(u,a.xy-q.xy));
    }
    if (Q.w < 1e-2) Q.z *= 0.9;
    Q.y -= 4e-2*Q.w;//gravity

    }
}
