//change these constants to get different patterns!
#define c2 0.0

#define c1 vec4(3.0+c2,.5+c2,1.5,0)
//#define c1 vec4(2.0+c2,1.5+c2,1.4,0)
//#define c1 vec4(1.0,1.5,1.4,0)
//#define c1 vec4(7.0,5.0,1.4,0)
//#define c1 vec4(7.0,9.0,1.4,0)
//#define c1 vec4(5.0,5.5,1.4,0)


vec2 triangle_wave(vec2 a,float scale){
    return abs(fract((a+c1.xy)*scale)-.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);    
    vec3 col;  
    float t1 = 4.5*64.;

    vec2 uv = (fragCoord-iResolution.xy)/iResolution.y/t1/2.0;
    uv += vec2(iTime/2.0,iTime/3.0)/t1/8.0;
    float t2 = floor((iTime/2.+uv.x)/10.0)/t1;

    float offset = .5;
    float scale2 = 1.5;
    float bend = 1.;
    float scale = c1.z;
    for(int i=0;i<6;i++)
    {
        vec2 t2 = vec2(0);
        vec2 t3 = vec2(0);
        for(int k = 0; k < 3; k++){
            //bend = 1.+col.x;
            uv /= -scale2;
            uv -= t2.yx/(scale);
            t2 = triangle_wave(-uv.yx-offset,scale)/bend;
            t3 = triangle_wave(-uv,scale)*bend;
            uv.yx = t2+t3;
        }
        col.x = abs((uv.x)-(uv.y)-col.x);
        col = col.yzx;
    }

    fragColor = vec4(vec3(col),1.0);

}
