// Amoeba Leopard

// i was playing with Conway's game of life cellular automaton
// thinking i had an intuition for a smooth fading version
// with a lifetime gradient and a neighbor ratio

// found by accident that it can make reaction diffusion patterns
// got overwhelmed but couldn't posted another turing pattern feedback
// so i went a bit too far and now it's weird enough as i like it

// iteration from Wasp Blanket https://www.shadertoy.com/view/NlVBz1
// and Platic Cream https://www.shadertoy.com/view/stKBRh

#define T(uv) abs(texture(iChannel0,uv).b)
#define N(v) normalize(v)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec4 frame = texture(iChannel0, uv);

    // tint
    vec3 tint = .5 + .5 * cos(vec3(1,2,3)*5. + frame.a*.4);
    fragColor = vec4(tint * frame.r, 1);


    // normal
    float height = 1.;
    vec3 unit = vec3(4./iResolution.xy, 0);
    vec3 normal = normalize(vec3(T(uv+unit.xz)-T(uv-unit.xz),
                                 T(uv-unit.zy)-T(uv+unit.zy),
                                 T(uv) * height));

    // light
    fragColor += vec4(.5) * clamp(dot(normal, N(vec3(-1,4,1))), 0., 1.);
    fragColor += frame.g * vec4(.5) * (1.-pow(clamp(dot(normal, N(vec3(0,0,2))), 0., 1.), 100.));

    // shadows
    fragColor -= vec4(0.5) * clamp(dot(normal, N(vec3(0,-4,1))), 0., 1.);
    fragColor *= pow(max(abs(frame.b)-.02,.0), .5);
}

const float speed = 0.5;
const float turbulences = 1.5;
const float attraction = 2.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if (iFrame < 1)
    {
        fragColor = vec4(hash23(vec3(fragCoord, 0.)), 0, 0);
        return;
    }

    // coordinates
    vec2 uv = fragCoord/R;
    vec2 mouse = (iMouse.xy - R.xy / 2.)/R.y;
    vec2 p = (fragCoord.xy - R.xy / 2.)/R.y;
    vec2 offset = vec2(0);
    float dist = length(p);

    vec4 buffer = texture(iChannel0, uv);

    // turbulences
    float noise = fbm(vec3(p, dist - iTime*.05*speed));
    noise = sin(noise * 6.28 + iTime * .05*speed);
    noise = pow(max(0., noise+.9), 0.5);

    // slide along normal
    vec2 normal = texture(iChannel1, uv).xy;
    normal.x *= -1.;
    offset += normal;

    float dt = 30. * iTimeDelta;

    // displace frame buffer
    vec4 frame = texture(iChannel0, uv + dt * offset * speed / R);

    // edge spawn
    bool spawn = fragCoord.x < 1. || fragCoord.x > R.x-1.
              || fragCoord.y < 1. || fragCoord.y > R.y-1.
              || noise < .1
              || (iMouse.z > .0 && length(p-mouse) < 50./R.y);

    // spawn from noise
    vec2 rng = hash23(vec3(fragCoord, iFrame));
    if (spawn) frame = vec4(step(0.5, rng.x),step(0.5, rng.y),0,0);

    // neighbor values
    vec2 neighbors = vec2(0);
    for (float x = -1.; x <= 1.; ++x)
    {
        for (float y = -1.; y <= 1.; ++y)
        {
            if (x == 0. && y == 0.) continue;
            neighbors += texture(iChannel0, uv+vec2(x,y)/R).rg;
        }
    }

    // animation fade
    frame.r += 2.0 * (neighbors.r > 4.0 ? 1. : -1.) * iTimeDelta;
    frame.g += 1.0 * (neighbors.g > 4.0 ? 1. : -1.) * iTimeDelta;

    fragColor = vec4(clamp(frame.rg, 0., 1.), noise, frame.a + (neighbors.r + neighbors.g) * iTimeDelta * .1);
}

#define R iResolution.xy

// Dave Hoskins https://www.shadertoy.com/view/4djSRW
float hash13(vec3 p3)
{
	p3  = fract(p3 * .1031);
    p3 += dot(p3, p3.zyx + 31.32);
    return fract((p3.x + p3.y) * p3.z);
}
vec2 hash23(vec3 p3)
{
	p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}

float gyroid (vec3 seed)
{
    return dot(sin(seed),cos(seed.yzx));
}

float fbm (vec3 seed)
{
    float result = 0.;
    float a = .5;
    for (int i = 0; i < 4; ++i) {
        seed += result * .5;
        result += gyroid(seed/a)*a;
        a /= 2.;
    }
    return result;
}

#define T(uv) abs(texture(iChannel0,uv).b)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    // normal
    float height = 1.;
    vec3 unit = vec3(50./iResolution.xy, 0);
    vec3 normal = normalize(vec3(T(uv+unit.xz)-T(uv-unit.xz),
                                 T(uv-unit.zy)-T(uv+unit.zy),
                                 T(uv) * height));

    fragColor = vec4(normal, 1);
}
