// variant of https://shadertoy.com/view/7lKfzh
// inspired by "Path with max sum" by Envy24. https://shadertoy.com/view/7lGfz1
// redone with buffer (and thus progressive )

#define n 10 // number of paths

void mainImage(out vec4 O, vec2 U)
{
    U = floor(N*U/R.y);
    vec2 P[n];
    for (int i=0; i<n; i++) P[i][i%2] += float(i*5);
    O-=O;
    for (float i = 0.; i < N+N*R.x/R.y -2. ; i++ ) {
        for (int i=0; i<n; i++) 
            P[i][ T(P[i]+vec2(1,0)).g < T(P[i]+vec2(0,1)).g ? 1 : 0 ] ++,
            P[i]==U ?  O += .6 + .6 * cos( 6.3*float(i)/float(n) + vec4(0,23,21,0) ) : O;
      }    
   if (O!=O-O) return;
   O = iMouse.z > 0. 
          ? .5+.5*sin(T(U).gggg) // T(U).gggg / (N*length(R)/R.y)
          : T(U).rrrr;

// O.b *= float( max(U.x,U.y) < float(iFrame%60) );
}

float N = 64.;

#define R    iResolution.xy
#define T(U) texelFetch(iChannel0, ivec2(U), 0 )

// precompute max sum field array ( opposite of distance field )
// .r = randow value
// .g = local max sum field

void mainImage( out vec4 O, vec2 U )
{
    if ( U.y > N || U.x > N*R.x/R.y ) return;  // only compute at resolution N

    U = floor(U);
    if (iFrame%120 < 1 ) { O*=0.; O.r = fract(1e4*sin(dot(U,R-57.)-iTime)); return; } // init

    O = T(U);                        // previous state
    O.g = O.r;

#if 0                                // purely local computations
    float l = U.x>0. ? T(U-vec2(1,0)).g : 0., 
          b = U.y>0. ? T(U-vec2(0,1)).g : 0.;
    O.g += U.x==0. ? b
         : U.y==0. ? l
         :           max(l,b);   
#else                                // boost(?) progression by directly computing borders
         if (U.x==0.) for (float y=0.; y < U.y; y++ ) O.g += T(U+vec2(0,y)).r;
    else if (U.y==0.) for (float x=0.; x < U.x; x++ ) O.g += T(U+vec2(x,0)).r;
    else O.g += max( T(U-vec2(1,0)).g, T(U-vec2(0,1)).g ); // progressively computing the max sum field
#endif
}
