#define count 400 //数量
#define BlizardSpeed 0.4 //风速
float rand(float x){
    return fract(
                sin(
                    dot(vec2(x + 47.49, 38.2467 / (x + 2.3)),vec2(12.9898, 78.233))
                    )
                );
}
float drawCircle(vec2 uv,vec2 center,float radius){
    return 1.0 - smoothstep(0.0,radius,length(uv -center));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
       fragColor = texture(iChannel0,uv);
    for(int i=0;i<count;i++){
        float j = float(i);
        float speed = 0.4 + rand(j) * (0.7 + 0.5 * cos(j / (float(count) * 0.25)));//这里得到下落速度和雪花大小
        vec2 center =vec2(
             rand(j) + uv.y * BlizardSpeed + 0.6 * cos(iTime + sin(j)),//x代表雪花的出生x轴坐标
             mod(rand(j) - speed *iTime,0.95)//y代表随时间下落的坐标
            );
             fragColor += vec4(drawCircle(uv,center,0.01*speed ));//这里传入speed 是作为大小使用
    }


}
