//original : https://www.shadertoy.com/view/7tffDj by ronwnor

const float tau = 6.28; 

mat2 rot(float a) {
    return mat2(
        cos(a), sin(a),
        -sin(a), cos(a));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    float zoom = 1.;

    vec2 u1 = (2.*fragCoord - iResolution.xy)/iResolution.y * zoom;

	vec2 mouse = iMouse.xy/iResolution.xy*2.-1.;

    if (mouse == vec2(-1)) mouse = vec2(-0.26,0.85); 

    float val = 0.; 

    //loop (originally i < 64)
    for(float i = 0.; i < 15. && val == 0.; i++){

        u1 = mouse + vec2(u1.x*u1.x - u1.y*u1.y,2.*u1.x*u1.y);       
        vec2 u2 = u1*rot(3.1415692*(iTime*0.1+i));

        if (u2.y >= 8.) {
            val = 0.01 + i/64. + 1./u2.y;
        }
    }

    vec3 col = 0.5 + 0.5*cos(tau*(val+.4+vec3(.0,.1,.2)));

    // Output to screen
    fragColor = vec4(col,1.0);
}
