#define sat(a) clamp(a, 0.,1.)
mat2 r2d(float a){float ca = cos(a),sa=sin(a);return mat2(ca,-sa,sa,ca);}

float _sqr(vec2 p, vec2 s)
{
  vec2 l = abs(p)-s;
  return max(l.x,l.y);
}


float line(vec2 p, vec2 s)
{
  vec2 rep = p;
  rep.y= abs(p.y);
  return min(_sqr(p, s),length(rep-vec2(0.,s.y))-s.x);
}
float pioupiou(vec2 p)
{
  return min(max(length(p)-.3,-(length(p-vec2(0.,.4))-.4)),
    length(p-vec2(.15,.12))-.17);
}

vec3 foot(vec2 p, vec3 col, float off)
{
  float shp = 400.;
  float h = .1;
  p-= vec2(0.,-.2);
  p*= r2d(sin(iTime*10.+off));
  p+= vec2(0.,h);


  float trunk = line(p,vec2(.02,h));
  trunk = min(trunk, line(p-vec2(0.,-.1),vec2(.01,.05)));
  vec2 mp = p;
  mp.x = abs(mp.x);

trunk = min(trunk, line((mp-vec2(0.,-.1))*r2d(-1.57*.5),vec2(.01,.05)));

  col = mix(col, vec3(.8,.6,.3),1.-sat(trunk*shp));
  return col;
}

vec3 grass(vec2 uv, float t, vec3 col, vec3 rgb)
{
  float freq = 80.;
  float shape  = uv.y-asin(sin(uv.x*freq+t))*.05;
  col = mix(col, rgb,1.-sat(400.*shape));
  return col;
}

float cloud(vec2 uv)
{
  uv.y+= -.5;
  uv.x+=iTime*.3;
  float rep = 0.1;
  float id = floor((uv.x+rep*.5)/rep);
  uv.x= mod(uv.x+rep*.5,rep)-rep*.5;
  float acc = -uv.y;

  acc = max(acc, length(uv)-abs(sin(id*.15))*.1);
  return acc;
}

vec3 rdr(vec2 uv)
{
  vec2 ouv = uv;
  vec3 col = vec3(.2,.5,.7);
  float shp = 400.;
  mat2 rotb = r2d(sin(iTime*15.)*.05);
  float piou = pioupiou(uv*rotb);
  col = grass(ouv-vec2(0.,-.3), iTime*10.,col,vec3(.1,.5,.3));
  col = foot(uv,col,3.1415);
  col = mix(col, vec3(.8,.7,.4), 1.-sat(shp*piou));
  col = mix(col, vec3(.1,.1,.2), 1.-sat(shp*(abs(piou)-.01)));
  col = mix(col, vec3(.1,.1,.2), 1.-sat(shp*(length((uv*rotb-vec2(.22,0.17)))-.02)));
  col = foot(uv,col,0.);
  uv*= rotb;
  uv -= vec2(.35,.13);
  float mouth = max(_sqr(uv, vec2(.05,.025)),-(_sqr(uv*r2d(1.57*.5)-vec2(.05),vec2(.05))));
  col = mix(col, vec3(.8,.6,.3), 1.-sat(shp*mouth));
  col = grass(ouv-vec2(0.1,-.4), iTime*20.,col,vec3(.1,.5,.3)*1.5);
col = grass(ouv-vec2(0.,-.6), iTime*30.,col,vec3(.1,.5,.3));
col = mix(col, vec3(1.), 1.-sat(cloud(ouv)*400.));
  return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 uv = (fragCoord.xy-vec2(.5)*iResolution.xy) / iResolution.xx;
  vec3 col = rdr(uv*2.5);
  fragColor = vec4(col, 1.0);
}
