vec3 getcolor(vec2 uv, float a, float b, vec3 color){
   float f = step(a, uv.x) * step(uv.x, b);
   return color * f;
}

vec3 getcolor1(vec2 uv){
    vec3 c1 = getcolor(uv, 0.0, 0.2, vec3(1.0,0.0,0.0));
    vec3 c2 = getcolor(uv, 0.2, 0.4, vec3(0.0,1.0,0.0));
    vec3 c3 = getcolor(uv, 0.4, 0.6, vec3(0.0,0.0,1.0));
    vec3 c4 = getcolor(uv, 0.6, 0.8, vec3(1.0,0.0,1.0));
    vec3 c5 = getcolor(uv, 0.8, 1.0, vec3(0.0,1.0,1.0));
    return c1 + c2 + c3 + c4 + c5;
}

vec3 getcolor2(vec2 uv){
    int i = int(5.0 * uv);

    return vec3(i==0||i == 3,i==1 || i==4, i>1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Output to screen
    fragColor = vec4(getcolor2(uv) ,1.0);
}
