#define CLOUDS 1
#define cloudSpeed 0.02
#define cloudHeight 3000.0
#define cloudThickness 1000.0
#define cloudDensity 0.01

#define fogDensity 0.0001

#define volumetricCloudSteps 32			//Higher is a better result with rendering of clouds.
#define volumetricLightSteps 1			//Higher is a better result with rendering of volumetric light.

#define cloudShadowingSteps 1			//Higher is a better result with shading on clouds.


#define rayleighCoeff (vec3(0.27, 0.5, 1.0) * 1e-5)	//Not really correct
#define mieCoeff vec3(0.5e-6)						//Not really correct

const float sunBrightness = 10.0;

#define earthRadius 6371000.0
float waterlevel = 70.0;        // height of the water
float wavegain   = 1.0;       // change to adjust the general water wave level
