const float WINDOW_WIDTH = 15.0;

#define AA_SPAN 1.5

#define ASPECT (iResolution.y/iResolution.x)

vec2 cmod(vec2 pos, vec2 off) {
    return mod(pos+0.5, off) - 0.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 win = WINDOW_WIDTH * (fragCoord/iResolution.xy - 0.5) * vec2(1, ASPECT);
    float ang = 3.0 * sin(iTime*1.0 + 5.0* sin(iTime*0.23));
    vec2 rot_pos = vec2(cos(ang)*win.x-sin(ang)*win.y,sin(ang)*win.x+cos(ang)*win.y);
    vec2 warp_centre = 3.0 * vec2(sin(iTime*2.0), cos(5.0*iTime));
    vec2 to_warp = rot_pos - warp_centre;
    float d_warp = length(to_warp);
    d_warp += (sin(iTime*3.5+ 2.3* sin(iTime*0.33))+1.2)*0.02*( pow(d_warp, 3.0) + 0.1 );
    vec2 warped = warp_centre +d_warp * normalize(to_warp);

    vec2 pos = cmod(warped, vec2(1));
    float dist = length(pos) - 0.25;

    // smoothstep based antialiasing thanks to @FabriceNeyret2
    float level = smoothstep(0.0, WINDOW_WIDTH*AA_SPAN/iResolution.x, dist);
    vec3 col = vec3(level);

    // Output to screen
    fragColor = vec4(col,1.0);
}
