// object transformation
vec3 rotate_x(vec3 p, float phi)
{   float c = cos(phi);	float s = sin(phi);
    return vec3(p.x, c*p.y - s*p.z, s*p.y + c*p.z);
}
vec3 rotate_y(vec3 p, float phi)
{	float c = cos(phi);	float s = sin(phi);
	return vec3(c*p.x + s*p.z, p.y, c*p.z - s*p.x);
}
vec3 rotate_z(vec3 p, float phi)
{	float c = cos(phi);	float s = sin(phi);
	return vec3(c*p.x - s*p.y, s*p.x + c*p.y, p.z);
}
///------------------------------------
vec2 rotatev2(vec2 p, float ang)
{   float c = cos(ang);
    float s = sin(ang);
    return vec2(p.x*c - p.y*s, p.x*s + p.y*c);
}



vec3 opAngRep( vec3 p, float a )
{
	vec2 polar = vec2(atan(p.y, p.x), length(p.xy));
    polar.x = mod(polar.x + a / 2.0, a) - a / 2.0;

    return vec3(polar.y * vec2(cos(polar.x),sin(polar.x)), p.z);
}

float sdTriPrism( vec3 p, vec2 h )
{
  vec3 q = abs(p);
  return max(q.z-h.y,max(q.x*0.866025+p.y*0.5,-p.y)-h.x*0.5);
}

float sdRoundCone( vec3 p, float r1, float r2, float h )
{
  vec2 q = vec2( length(p.xz), p.y );

  float b = (r1-r2)/h;
  float a = sqrt(1.0-b*b);
  float k = dot(q,vec2(-b,a));

  if( k < 0.0 ) return length(q) - r1;
  if( k > a*h ) return length(q-vec2(0.0,h)) - r2;

  return dot(q, vec2(a,b) ) - r1;
}


vec3 planeta(vec3 p)
{   vec3 res= vec3(9999.0, -1.0,-1.0);
    float sds1 =sdSphere(p- vec3(0.0,0.0,0.0), 15.0 );
    float sdc1 =sdCylinder(p- vec3(0.0,0.0,0.0) , vec2(30.0,0.5) );

    float sdu1 =unionSDF(sds1,sdc1);
    res=opU(res, vec3(sdu1,-1.0,1.0));
    return res;
}    

vec3 Piramidepicos(vec3 p)
{
    vec3 res= vec3(9999.0, -1.0,-1.0);
    float sdt1= sdTriPrism( p-vec3(12.5,-1.0,30.0), vec2(8.0,5.0));
