vec3 GetDist(vec3 p  ) 
{	vec3 res= vec3(9999.0, -1.0,-1.0);  vec3 pp=p;
	float planeDist1 = p.y+0.0;  //piso inf
    res =opU3(res, vec3(planeDist1,-1.0,7.0)); //inf
      p.y=p.y-5.0;
    vec3 p3=p-vec3(0.0,25.0,65.0);
    vec3 p5=p;
    p3=rotate_x( p3, radians(45.0));

    p3.x= opRep1D(p3.x, 140.0 );
    vec3 pl1= planeta(p3);
    res =opU3(res, pl1);

    p5.x= opRep1D(p5.x, 50.0 );
    vec3 pip1= Piramidepicos(p5);
    res =opU3(res, pip1);


    vec3 p1=p;vec3 p2=p;

    p1=p1-vec3(0.0,22.0,40.0);
    p2=p2-vec3(30.0,22.0,40.0);

    p1.x= opRep1D(p1.x, 65.0 );
    p2.x= opRep1D(p2.x, 65.0 );

    p1=rotate_z( p1, radians(25.0));
    p2=rotate_z( p2, radians(335.0));

    float sdb1= sdBox( p1, vec3(2.0,60.0,2.0) );
    float sdb2= sdBox( p2, vec3(2.0,60.0,2.0) );

    res =opU3(res, vec3(sdb1,1.0,MATERIAL_NO));
    res =opU3(res, vec3(sdb2,2.0,MATERIAL_NO));

    //res =opU3(res, vec3(1.0,0.0,MATERIAL_NO)); 
    //return (dist, id_color, id_material)
    return res;
}

