#define opU2(d1, d2) ( d1.x < d2.x ? d1 : d2 )

float sdSphere( vec3 p, float s )
	{ return length(p)-s;}
float sdBox( vec3 p, vec3 b )
	{ vec3 d = abs(p) - b;   return length(max(d,0.0))+ min(max(d.x,max(d.y,d.z)),0.0); }

float Intersect(float distA, float distB)
	{ return max(distA, distB);}
float Union(float distA, float distB)
	{ return min(distA, distB);}
float Difference(float distA, float distB)
	{ return max(distA, -distB);}

vec2 GetDist(vec3 p  ) 
{	vec2 res= vec2(9999.0, -1.0);  
     p.y=p.y+10.0;
    vec3 p0=p;
	float planeDist1 = p.y+10.0;  //piso inf
    float planeDist2 = 50.0-p.y;  //piso sup
    float planeDist3 = p.x+40.0; //pared izq
    float planeDist4 = 40.0-p.x;  //pared der
    float planeDist5 = -p.z+50.0;  //pared frente
    float planeDist6 = p.z+50.0;  //pared atras
    res =opU2(res, vec2(planeDist1,57.0));
    p.y=p.y-8.0;
    p.z-=30.0;
    float d1a;
    float t=iTime+2.5;
    if(5.0*sin(t+2.0*cos(t*0.5))>0.0)
     d1a= sdBox(p-vec3(0.0,0.0,5.0), vec3(18.0,14.0,40.0) );
    else
     d1a= sdBox(p-vec3(0.0,0.0,0.0), vec3(18.0,14.0,40.0) );

    float d1b= sdBox(p, vec3(17.0,13.0,42.0) );

    float ddif1= Difference(d1a,d1b);
    float d2=sdSphere(p-vec3(0.0,abs(5.0*sin(iTime)),0.0) , 5.0 );
    float d3=sdSphere(p-vec3(10.0,0.0,0.0), 5.0 );
    res =opU2(res, vec2(ddif1,1.0));
    res =opU2(res, vec2(d2,41.0));
    res =opU2(res, vec2(d3,100.0));

    return res;
}

vec3 GetNormal(vec3 p)
{   float d = GetDist(p).x;
    vec2 e = vec2(.001, 0);
    vec3 n = d - vec3(GetDist(p-e.xyy).x,GetDist(p-e.yxy).x,GetDist(p-e.yyx).x);
    return normalize(n);
}

vec2 RayMarch(vec3 ro, vec3 rd, int PMaxSteps)
{   vec3 p;
    vec2 hit, object=vec2(0.1,0.0);
    for(int i=0; i <= PMaxSteps; i++) 
    { p = ro + rd*object.x;
      hit = GetDist(p);
      object.x += hit.x;
      object.y = hit.y;
      if (abs(hit.x) < EPSILON || object.x > MAX_DIST) break;
    }    
    return object;
}

float FakeSoftShadow(float result, vec3 LightPos,vec3 point,vec3 normal)
{   vec3 shadowOffset = normal * 0.2 ;
    int epoch = 5;

    float delta = result/float(epoch-1);
    float lightOffset = 0.2;
    float dis;
    for(int i = 0; i<epoch; i++)
    {   vec3 tmp = LightPos +vec3(0,0,i)*lightOffset;
        dis=RayMarch(point + shadowOffset,normalize(tmp-point), int( MAX_DIST/10.)).x;
        if (dis< length(tmp-point))
        {  result -= delta; }
    }
    return result;
}

float ShadingLVNHR(vec3 LightPos, vec3 pos, vec3 L, vec3 V, vec3 N, vec3 H, vec3 R)
{
    float Ambient = 0.5;       
    float result = max(dot(L,N),0.0)*0.5 + pow(max(dot(H,N),0.0),20.)*0.9;
   result = FakeSoftShadow(result, LightPos,pos,N);
    return result+Ambient;

}

float getSoftShadow(vec3 p, vec3 lightPos) {
    float res = 9999.0;
    float dist = 0.01;
    float lightSize = 0.03;
    for (int i = 0; i < MAX_STEPS; i++) {
      float hit = GetDist(p + lightPos * dist).x;
      res = min(res, hit / (dist * lightSize));
      dist += hit;
      if (hit < 0.0001 || dist > 60.0) break;
    }
    return clamp(res, 0.0, 1.0);
}

float occlusion(vec3 pos, vec3 nor)
{   float sca = 2.0, occ = 0.0;
    for(int i = 0; i < 10; i++) {    
      float hr = 0.01 + float(i) * 0.5 / 4.0;
      float dd = GetDist(nor * hr + pos).x;
      occ += (hr - dd)*sca;
      sca *= 0.6;
    }
    return clamp( 1.0 - occ, 0.0, 1.0 );    
}

//iq cosine based palette, 4 vec3 params
vec3 palette( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

vec3 lightingv3(vec3 normal,vec3 p, vec3 lp, vec3 rd, vec3 ro,vec3 lightColor, float t) 
{   vec3 lightPos=lp;
    vec3 worldPos = p;

    vec3 L = normalize (lightPos - p);
    vec3 V = normalize(ro - p);
    vec3 N = normal;
    vec3 H = normalize(L+V);
    vec3 R = normalize(reflect(-L, N));

    float lightDist = max(length(L), .001);
    float atten=1.0 / (1.0 + lightDist * 0.125 + lightDist * lightDist * .05);
    L /= (lightDist*atten);

    float shadow=ShadingLVNHR(lightPos,p,L, V,N,H,R);

    float occ = occlusion(worldPos, N);// occ
    vec3 ambient = Ka + Ka * dot(normal, vec3(0., 1., 0.))*lightColor;
    ambient*=0.5;
    vec3 fresnel =  lightColor *  pow(clamp(1.0 + dot(rd, N), 0.0, 1.0), 2.0);;
    float diff= clamp(dot(N, L), 0.0, 1.0);
    vec3 diffuse =  lightColor * diff;
    float shininess=10.0;
    float specular    = pow(max(dot(R, V), 0.0), shininess);

    vec3 back = 0.5 * lightColor * clamp(dot(N, -L), 0.0, 1.0); // back
    vec3 colOut = occ*lightColor*(ambient+diffuse*shadow+.25 +back) + vec3(.7,.9,1)*specular*specular;
    return colOut;
}

vec3 getColorTextura( vec3 p, vec3 nor,  int i)
{	vec3 col;
    if (i==100 )
    { col=tex3D(iChannel0, p/32., nor); return col*2.0; }
