vec3 linear2srgb(vec3 c) 
{ return mix(12.92 * c,1.055 * pow(c, vec3(1.0/1.8)) - 0.055, step(vec3(0.0031308), c)); }

vec3 exposureToneMapping(float exposure, vec3 hdrColor) 
{ return vec3(1.0) - exp(-hdrColor * exposure); }

vec3 ACESFilm(vec3 x)
{   float a,b,c,d,e;
    a = 2.51; b = 0.03; c = 2.43;d = 0.59; e = 0.14;
    return (x*(a*x+b))/(x*(c*x+d)+e);
}


vec3 Render(vec3 ro, vec3 rd)
{  vec3 col = vec3(0);
  vec3 p;
  float multc=1.0;
     vec2 hit=RayMarch(ro,rd, MAX_STEPS);
      if(hit.x<MAX_DIST)
       {   p = (ro + rd * hit.x );
        vec3 nor=GetNormal(p);
        vec3 colobj;
        colobj=GetMaterial( p, nor, ro, rd,  int(hit.y));
        vec3 result;

        float t=iTime+3.0;
        if(int(hit.y)==1   )
        { multc=0.3+0.2*sin(t+cos(t)+1.0); }

        if( int(hit.y)==100 || int(hit.y)==41 )
        { multc=0.650+0.2*sin(t+cos(t)); }


        result=  lightingv3(nor, p,light_pos1, rd,ro,colobj,hit.x)*light_color1*multc;
        result+= lightingv3(nor, p,light_pos2,rd, ro,colobj,hit.x)*light_color2*multc;
        col= result/2.0;
        col= (ACESFilm(col)+linear2srgb(col)+col+ exposureToneMapping(3.0, col))/4.0 ;

    }
    else if(hit.x>MAX_DIST)
    col= render_sky_color(rd);
   return col;
}

//https://www.shadertoy.com/view/7dKfRt
mat3 camera(vec3 cameraPos, vec3 lookAtPoint) {
	vec3 cd = normalize(lookAtPoint - cameraPos); // camera direction
	vec3 cr = normalize(cross(vec3(0, 1, 0), cd)); // camera right
	vec3 cu = normalize(cross(cd, cr)); // camera up

	return mat3(-cr, cu, -cd);
}

mat3 cameraToWorld(vec3 ro,vec3 lookAt){
    vec3 a = normalize(lookAt - ro);
    vec3 b = cross(vec3(0.,1.,0),a);
    vec3 c = cross(a,b);
    return mat3(b,c,a);
}

