vec3 palette[7];
vec3 getsmcolor(float c, float s) 
{
    s*=.5;
    c=mod(c-.5,7.);
    vec3 color1=vec3(0.0),color2=vec3(0.0);
    for(int i=0;i<7;i++) {
        if (float(i)-c<=.0) {
            color1 = palette[i];
            color2 = palette[(i+1>6)?0:i+1];
        }
    }
    // smooth mix the two colors
    return mix(color1,color2,smoothstep(.5-s,.5+s,fract(c)));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float fx = iResolution.x/iResolution.y;

    uv.x*=fx;
    vec2 p = vec2(0.5*fx,.5) - uv;
    float r = length(p);
    float a = atan(p.x,p.y);
	palette[6]=vec3(200,000,000)/255.;
	palette[5]=vec3(255,127,000)/255.;
	palette[4]=vec3(255,200,100)/255.;
	palette[3]=vec3(50,200,100)/255.;
	palette[2]=vec3(000,000,255)/255.;
	palette[1]=vec3(075,000,130)/255.;
	palette[0]=vec3(143,000,255)/255.;

    vec3 color = vec3(0.);



    float forma = sin(r*5.+sin(a*10.+sin(r*10.))*.2)*sin(r*2.-iTime);

    forma*=sin(forma*10.+iTime)+sin(a*4.);
    forma = r;
    color = getsmcolor(forma-iTime*.5, .99); 



    fragColor = vec4(color,1.0);
}
