// Display ðŸ¦œ
Main {
    vec4 b = B(U);
    vec4 n = B(U+vec2(0,1));
    vec4 e = B(U+vec2(1,0));
    vec4 s = B(U-vec2(0,1));
    vec4 w = B(U-vec2(1,0));
    b = 1./4.*(n+e+s+w);
    Q = 1.-1.4*b*vec4(1.1,1,1.3,1);
}

#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)
#define Main void mainImage(out vec4 Q, in vec2 U)
#define ei(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define Z .73
#define M .125
vec3 hash(vec3 p3)
    {
        p3 = fract(p3 * vec3(.1031, .1030, .0973));
        p3 += dot(p3, p3.yxz+33.33);
        return fract((p3.xxy + p3.yxx)*p3.zyx);

    }

// Translate Particles ðŸš—
Main {
    Q = vec4(0);
    vec4 QB = vec4(0);
    for (float x=-2.;x<=2.;x++)
    for (float y=-2.;y<=2.;y++)
    {
          vec2 xy = vec2(x,y);
          vec4 b = B(U+xy);
          if (b.w>0.) {
              vec4 a = A(U+xy);

              vec4 _1 = a, _2 = a;
              mat2 m = ei(6.2*hash(vec3(U+xy,iTime)).x);
              if (b.w>M) {
                  vec2 v = vec2(0,Z)*m;
                  _2.xy -= v;
                  _1.xy += v;
              }
              if (_1.x<U.x+.5&&_1.x>=U.x-.5&&
                  _1.y<U.y+.5&&_1.y>=U.y-.5){
                  Q += .5*b.w*_1;
                  QB.w += .5*b.w; 
              }
              if (_2.x<U.x+.5&&_2.x>=U.x-.5&&
                  _2.y<U.y+.5&&_2.y>=U.y-.5){
                  Q += .5*b.w*_2;
                  QB.w += .5*b.w;
              }
         }
    }
    if (QB.w >0.) Q /= QB.w;
}

// Translate Particles ðŸš—
Main {
    Q = vec4(0);
    vec4 QB = vec4(0);
    for (float x=-2.;x<=2.;x++)
    for (float y=-2.;y<=2.;y++)
    {
          vec2 xy = vec2(x,y);
          vec4 b = B(U+xy);
          if (b.w>0.) {
              vec4 a = A(U+xy);

              vec4 _1 = a, _2 = a;
              mat2 m = ei(6.2*hash(vec3(U+xy,iTime)).x);
              if (b.w>M) {
                  vec2 v = vec2(0,Z)*m;
                  _2.xy -= v;
                  _1.xy += v;
              }
              if (_1.x<U.x+.5&&_1.x>=U.x-.5&&
                  _1.y<U.y+.5&&_1.y>=U.y-.5){
                  Q += .5*b.w*_1;
                  QB.xyz += .5*b.w*b.xyz;
                  QB.w += .5*b.w; 
              }
              if (_2.x<U.x+.5&&_2.x>=U.x-.5&&
                  _2.y<U.y+.5&&_2.y>=U.y-.5){
                  Q += .5*b.w*_2;
                  QB.xyz += .5*b.w*b.xyz;
                  QB.w += .5*b.w;
              }
         }
    }
    Q = QB;
    if (QB.w>0.)Q.xyz/=QB.w;
}

// Force ðŸŒŠ
Main {
    Q = A(U);
    vec4 QB = B(U);
    vec2 f = vec2(0);
    if (QB.w>0.)
    {

        for (float x = -2.;x<=2.;x++)
        for (float y = -2.;y<=2.;y++)
        if (x!=0.&&y!=0.)
        {
              vec2 xy = vec2(x,y);
              vec4 b = B(U+xy);
              f.xy -= 0.1*b.z*b.w*length(sign(b.xyz-.5)-sign(QB.xyz-.5))*xy/dot(xy,xy);
              f.xy -= 0.01*b.w*(b.w-3.*b.y)*xy/dot(xy,xy);
         }
        Q.zw += QB.x*f.xy/QB.w;//-1e-*Q.zw;
        Q.xy += Q.zw*.5*inversesqrt(
                1.+dot(Q.zw,Q.zw));
    }
    Q.w -= .02/R.y;


    if (Q.x<2.)Q.z=abs(Q.z),Q.x=2.;
    if (Q.y<2.)Q.w=abs(Q.w),Q.y=2.;
    if (R.x-Q.x<3.)Q.z=-abs(Q.z),Q.x=R.x-3.;
    if (R.y-Q.y<3.)Q.w=-abs(Q.w),Q.y=R.y-3.;
    if (iMouse.z>0.&&length(U.xy-iMouse.xy)<10.)
        Q = vec4(U+vec2(.5,0),.25,0);

    if (iFrame < 1) 
        Q = vec4(U,0,0);
}

// Color ðŸŒˆ
Main {
    Q = B(U);
    if (iMouse.z>0.&&length(U.xy-iMouse.xy)<10.)
        Q=.5+.5*sin(iTime+vec4(1,2,3,4)),
        Q.w = 1.;
    if (iFrame < 1) {
        Q = max(.5-.5*sin(U.y/R.y*40.+vec4(1,2,3,4)),0.);
        Q.w = step(U.y,.5*R.y);
    }
}
