vec3 opAngRep( vec3 p, float a )
{
	vec2 polar = vec2(atan(p.y, p.x), length(p.xy));
    polar.x = mod(polar.x + a / 2.0, a) - a / 2.0;

    return vec3(polar.y * vec2(cos(polar.x),sin(polar.x)), p.z);
}

vec3 segundaNave(vec3 p)
{
    vec3 pp=p;
    vec3 res= vec3(9999.0, -1.0,-1.0);

    float sp1= sdSphere( p, 12.0 );
    float sb1= sdBox( p-vec3(0.0,-6.5,0.0), vec3(12.5,7.0,12.5) );
    float dif= differenceSDF(sp1, sb1);


    float numItemA=10.0;
    float numItemB=6.0;
    float tau = atan(1.0) * 8.0;

    p= rotate_x( p-vec3(0.0,1.0,0.0), radians(90.0));
    vec3 rep2 = opAngRep(p, tau / numItemA);
    rep2= rotate_z( rep2-vec3(10.0,0.0,0.0), radians(270.0));

    pp= rotate_x( pp-vec3(0.0,-1.0,0.0), radians(90.0));
    vec3 rep3 = opAngRep(pp, tau / numItemB);
    rep3= rotate_x( rep3, radians(90.0));
    rep3= rotate_z( rep3, radians(340.0));

    float scc1= sdCappedCone( rep2, 9.0, 0.025, 1.2 );
    float scc2= sdCappedCone( rep3-vec3(7.0,1.0,0.0), 9.5, 1.2,0.025 );
    res =opU3(res, vec3(dif,1.0,MATERIAL_NO)); 
    res =opU3(res, vec3(scc1,32.0,-1.0));
    res =opU3(res, vec3(scc2,6.0,-1.0));
    return res;
}


