vec3 rotateXY(vec3 v, float a) { float c = cos(a), s = sin(a); return mat3( c, s, 0, -s, c, 0, 0, 0, 1 )*v; }
vec3 rotateXZ(vec3 v, float a) { float c = cos(a), s = sin(a); return mat3( c, 0, s, 0, 1, 0, -s, 0, c )*v; }
vec3 rotateYZ(vec3 v, float a) { float c = cos(a), s = sin(a); return mat3( 1, 0, 0, 0, c, s, 0, -s, c )*v; }

#define UI0 1597334673U
#define UI1 3812015801U
#define UI2 uvec2(UI0, UI1)
#define UI3 uvec3(UI0, UI1, 2798796415U)
#define UIF (1. / float(0xffffffffU))
float hash11(uint q)
{
	uvec2 n = q * UI2;
	q = (n.x ^ n.y) * UI0;
	return float(q) * UIF;
}

// change to NOISE_0 for greyscale
#define NOISE_1

vec3 noise(ivec2 pos) {
    #ifdef NOISE_0
    vec3 p = vec3(pos, uint(iTime*1.0));
	p=rotateXZ(p, 0.4+iTime*0.0123);
	p=rotateYZ(p, 0.3232+iTime*0.1);
    return vec3(
        hash11(uint(int(p.x) ^ int(p.y) ^ int(p.z)))
    );
    #elif defined NOISE_1
    vec3 p = vec3(pos, iTime*5.0);
	p=rotateXZ(p, 0.5+iTime*0.123);
	p=rotateYZ(p, 0.3232+iTime*0.05);
	vec3 col = vec3(0);
	col.r = hash11(uint(int(p.x) ^ int(p.y) ^ int(p.z)));
	p=rotateXZ(p, 0.02);
	p=rotateYZ(p, 0.04232);
	col.g = hash11(uint(int(p.x) ^ int(p.y) ^ int(p.z)));
	p=rotateXZ(p, 0.01);
	p=rotateYZ(p, 0.02);
	col.b = hash11(uint(int(p.x) ^ int(p.y) ^ int(p.z)));
    return col;
    #endif
}

void mainImage( out vec4 O, in vec2 U )
{
    U -= iResolution.xy/2.;
    U /= iResolution.y/2.;

	O.xyz = vec3(noise(ivec2(U*200.0)));
    O.w = 1.;
}
