vec2 Pisosdf01(vec3 pos)
{ 
  const float repeat = 20.0;
   float b= pos.y-0.0;
  pos.x= opRep1D( pos.x, repeat );
  pos.z= opRep1D( pos.z, repeat );
  float cz = sdCylinderZ(pos, vec3(0.0, 0.0, 10.0));
  float cx = sdCylinderX(pos, vec3(0.0, 0.0, 10.0));
  float c = min(cz, cx);   
  return vec2( differenceSDF(b,c),100.0);  
}

