const float pi = 3.1415926535897932384626433832795;

//	PRIMITIVES are from
//	https://iquilezles.org/articles/distfunctions

struct Material { 

    vec3  color;        // [0,1/pi] 

    float roughness;    // [0,~7] 

    vec3  emission;	    // [0, inf] 

    float metalness;    // 0.02-0.05 for non-metals, 0.6-0.9 for metals 

}; 

Material matarray[6] = Material[6](Material(vec3(0.1,0.2,0.3),0.0,vec3(1,1,1),0.8),
Material(vec3(0.1,0,0),2.0,vec3(3,3,3),0.6),
Material(vec3(0,0.2,0),5.0,vec3(0,0,0),0.02),
Material(vec3(0.1,0,0.3),6.0,vec3(1,2,1),0.6),
Material(vec3(0,0.2,0.3),0.0,vec3(1,1,8),0.03),
