float sdSphere( vec3 p, float s )
{
	return length(p)-s;
}

float sdBox( vec3 p, vec3 b )
{
	vec3 d = abs(p) - b;
	return length(max(d,0.0))+min(max(d.x,max(d.y,d.z)),0.0); // remove this line for an only partially signed sdf 
}

float sdBoxFrame( vec3 p, vec3 b, float e )
{
  p = abs(p  )-b;
  vec3 q = abs(p+e)-e;
  return min(min(
      length(max(vec3(p.x,q.y,q.z),0.0))+min(max(p.x,max(q.y,q.z)),0.0),
      length(max(vec3(q.x,p.y,q.z),0.0))+min(max(q.x,max(p.y,q.z)),0.0)),
      length(max(vec3(q.x,q.y,p.z),0.0))+min(max(q.x,max(q.y,p.z)),0.0));
}

float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

float sdHexPrism( vec3 p, vec2 h )
{
  const vec3 k = vec3(-0.8660254, 0.5, 0.57735);
  p = abs(p);
  p.xy -= 2.0*min(dot(k.xy, p.xy), 0.0)*k.xy;
  vec2 d = vec2(
       length(p.xy-vec2(clamp(p.x,-k.z*h.x,k.z*h.x), h.x))*sign(p.y-h.x),
       p.z-h.y );
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

///SDF

float sdf(in vec3 p)
{
    p-=vec3(0,0,2);
    float f = sdSphere(p-vec3(-4,1.5,0), 1.0);
    float g = sdBox(p-vec3(2,0,0),vec3(1,1,1));
    float h = sdBoxFrame(p-vec3(2,0,0),vec3(1.5,1,1.5),0.1);
    float i = sdTorus(p-vec3(-4,0,0),vec2(2,0.5));
    float j = sdBoxFrame(p-vec3(1,2,1),vec3(0.3,1,0.3),0.05);
    float k = sdBoxFrame(p-vec3(1,2,-1),vec3(0.3,1,0.3),0.05);
    float l = sdBoxFrame(p-vec3(3,2,1),vec3(0.3,1,0.3),0.05);
    float m = sdBoxFrame(p-vec3(3,2,-1),vec3(0.3,1,0.3),0.05);
    float o = sdHexPrism(p-vec3(2,3,0),vec2(2,0.6));


    return min(min(min(min(min(f,g),min(h,i)),min(min(j,k),min(l,m))),o),p.y+1.);
}

