    uvec2 I = uvec2( U/4.+ iTime*16.);
    O = vec4(1);
    bool b = false;
    for(uint i = 0u; i++ < 12u;I = i+(I+I.yx/uvec2(11,13)-I.yx/uvec2(13,11))*2u/3u){
        b = !b && bool(I.x & 1u | I.y & 1u);
        O.x = abs(float(b)-O.x);
        O.xyz = O.yzx-float(b)/2.;

        //some random-looking distortion (using prime numbers)
        //I += I.yx/(31u)-I.yx/(29u)+I.yx/(23u);
        //I /= 1u+uint(b);
    }
    if (!b) O = 1.-O;

}

#define fmod(x,y) floor(max(mod(floor(-(x))*4.,y),mod(floor(x)*3.,y)))
#define fmod1(x,y) floor(min(mod(floor(-(x))*4.,y),mod(floor(x)*3.,y)))
vec2 mainSound(int samp, float time){  
  //time *= 4.;
  float tempo = 1.;
  time /= tempo;
  float
  s1 = pow(2.,1.+fmod1(time/8.,3.)),
  s2 = pow(2.,1.+fmod(time/8.,3.)),
  m1 = s1*(1.+fmod(time/s2/s1+1.,2.)),
  t = time*tempo,
  m4 = fmod(t*s1/(1.+fmod1(t/s1,2.)),s2),
  m3 = fmod((t*s1+m4)*s1/m1,s2);
  t *= s1*s1/(1.+fmod(t/s1,2.));
  float m2 = 1. + fmod1(t*s1,s1);
  float a =
      //8.*pow((1.-sqrt(fract(t/s1*tempo)/m1))/4.,2.)
      log(1./8.+abs(fract(t/s1*tempo)))
  ;
  float nb = time*tempo*pow(2.,(m3+m2)/5.+7.5);
  return abs(.5-vec2(fract(nb*.998),fract(nb)))*a;
}
