//por jorge2017a1-
#define MAX_STEPS 100
#define MAX_DIST 100.
#define MIN_DIST 0.001
#define EPSILON 0.001

#define REFLECT 2

vec3 GetColorYMaterial(vec3 p,  vec3 n, vec3 ro,  vec3 rd, int id_color, float id_material);
vec3 getMaterial( vec3 pp, float id_material);

vec3 light_pos1;  vec3 light_color1 ;
vec3 light_pos2;  vec3 light_color2 ;

//operacion de Union  por FabriceNeyret2
#define opU3(d1, d2) ( d1.x < d2.x ? d1 : d2 )

#define opU2(d1, d2) ( d1.x < d2.x ? d1 : d2 )
#define opI2(d1, d2) ( d1.x > d2.x ? d1 : d2 )
#define opS2(d1, d2) ( d1.x > -d2.x ? d1 : d2 )

float sdSphere( vec3 p, float s )
	{ return length(p)-s;}
float sdBox( vec3 p, vec3 b )
	{ vec3 d = abs(p) - b;   return length(max(d,0.0))+ min(max(d.x,max(d.y,d.z)),0.0); }
float sdRoundBox( vec3 p, vec3 b, float r )
	{ vec3 q = abs(p) - b; return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r; }

///----------Operacion de Distancia--------
float intersectSDF(float distA, float distB)
	{ return max(distA, distB);}

float unionSDF(float distA, float distB)
	{ return min(distA, distB);}

float differenceSDF(float distA, float distB)
	{ return max(distA, -distB);}
//-------------------------------------------

// object transformation
vec3 rotate_x(vec3 p, float phi)
{
    float c = cos(phi);	float s = sin(phi);
    return vec3(p.x, c*p.y - s*p.z, s*p.y + c*p.z);
}
vec3 rotate_y(vec3 p, float phi)
{
	float c = cos(phi);	float s = sin(phi);
	return vec3(c*p.x + s*p.z, p.y, c*p.z - s*p.x);
}
vec3 rotate_z(vec3 p, float phi)
{
	float c = cos(phi);	float s = sin(phi);
	return vec3(c*p.x - s*p.y, s*p.x + c*p.y, p.z);
}
///------------------------------------
vec2 rotatev2(vec2 p, float ang)
{
    float c = cos(ang);
    float s = sin(ang);
    return vec2(p.x*c - p.y*s, p.x*s + p.y*c);
}
///------------------------------------
vec3 GetDist(vec3 p  ) 
{	
	vec3 res= vec3(9999.0, -1.0,-1.0);
    vec3 pp=p;

	float planeDist1 = p.y+0.0;  //piso inf
    float planeDist2 = 50.0-p.y;  //piso sup
    float planeDist3 = p.x+30.0; //pared izq
    float planeDist4 = 30.0-p.x;  //pared der
    float planeDist5 = -p.z+40.0;  //pared frente
    float planeDist6 = p.z+40.0;  //pared atras

    res =opU3(res, vec3(planeDist1,-1.0,5.0)); //inf


    res =opU3(res, vec3(planeDist2,14.0,MATERIAL_NO)); 
    res =opU3(res, vec3(planeDist3,100.0,MATERIAL_NO)); 
    res =opU3(res, vec3(planeDist4,100.0,MATERIAL_NO)); 
    res =opU3(res, vec3(planeDist5,100. ,MATERIAL_NO)); 
    res =opU3(res, vec3(planeDist6,16.0,MATERIAL_NO)); 


    p.y=p.y-5.0;
    float sdb1= sdBox(p-vec3(0.0,-1.0,0.0), vec3(15.0,1.0,20.0));
    float sdsp1= sdSphere( p- vec3(0.0,5.0,0.0), 2.0);
    float sdsp2= sdSphere( p- vec3(-8.0,3.0,0.0), 2.0);


     float posx=-6.5; //es el centro del objeto
     float posz=10.0;
     vec3 pp1=p-vec3(posx,0.0,posz);
     pp1.xz = rotatev2(pp1.xz, iTime);
     pp1.xy = rotatev2(pp1.xy, iTime);
     pp1.x=pp1.x+posx;

    float sdb2=  sdRoundBox( pp1-vec3(posx,8.0,0.0), vec3(2.0,1.0,2.0), 1.0 ); //3 in


     posx=6.5; //es el centro del objeto
     pp=p;
     pp.x=pp.x-posx;
     pp.xz = rotatev2(pp.xz, iTime);
     pp.x=pp.x+posx;

    float sdb3=  sdRoundBox( pp-vec3(posx,3.5,0.0), vec3(2.0,1.0,2.0), 1.0 ); //3 in
    res =opU3(res, vec3(sdb1,201.0, MATERIAL_NO));   
    res =opU3(res, vec3(sdb2,3.0, MATERIAL_NO));   
    res =opU3(res, vec3(sdsp1,2.0,MATERIAL_NO));   
    res =opU3(res, vec3(sdsp2,201.0,MATERIAL_NO));   
    res =opU3(res, vec3(sdb3,4.0,MATERIAL_NO));   



    //res =opU3(res, vec3(1.0,0.0,MATERIAL_NO)); 
    //return (dist, id_color, id_material)
    return res;
}


vec3 lightingv3(vec3 normal,vec3 p, vec3 lp, vec3 rd, vec3 ro) 
{
    vec3 l = lp - p;
    float dist = max(length(l), 0.01);
    float atten = min(1./(1. + dist*0.5), 0.2);
    l /= dist;

    vec3 n = normal;
   	vec3 r = reflect(-l, n);

    float dif = clamp(dot(l, n), 0.0, 1.0);
    float spe = pow(clamp(dot(r, -rd), 0.0, 1.0), 8.0);
    float fre = pow(clamp(1.0 + dot(n, rd), 0.0, 1.0), 2.0);
    float dom = smoothstep(-1.0, 1.0, r.y);

    vec3 lin = vec3(0.2);
    lin += 1.0*dif*vec3(1, .97, .85);
    lin += 2.5*spe*vec3(1, .97, .85)*dif;
    lin += 2.5*fre*vec3(1);
    lin += 0.5*dom*vec3(1);

    return lin*atten;
}

//------------------------------------------------
vec3 GetNormal(vec3 p)
{
	float d = GetDist(p).x;
    vec2 e = vec2(.001, 0);
    vec3 n = d - vec3(
        GetDist(p-e.xyy).x,
        GetDist(p-e.yxy).x,
        GetDist(p-e.yyx).x);
    return normalize(n);
}



float RayMarch(vec3 ro, vec3 rd){
    float dO = 0.; 
    vec3 dS=vec3(9999.0,-1.0,-1.0);

    float marchCount = 0.0;
    vec3 p;

    #define DISTANCE_BIAS 0.75
    float minDist = 9999.0; 

    for(int i=0; i <= MAX_STEPS; i++) 
    {  	p = ro + rd*dO;
        dS = GetDist(p);
        if ( abs(dS.x)<MIN_DIST  || i == MAX_STEPS)
        {   mObj.hitbln = true; minDist = abs(dO); break; }

        if(dO>MAX_DIST)
        { mObj.hitbln = false;    minDist = dO;    break; } 

        dO += dS.x;
        marchCount++;

    }

    mObj.dist = minDist;
    mObj.id_color = dS.y;
    mObj.marchCount=marchCount;
    mObj.id_material=dS.z;
    mObj.normal=GetNormal(p);
    mObj.phit=p;
    return dO;
}

//---------------------------------------------------
float GetShadow(vec3 p, vec3 plig)
{
    vec3 lightPos = plig;
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);
    float dif = clamp(dot(n, l), 0., 1.);
    float d = RayMarch(p+n*MIN_DIST*2., l );
    if(d<length(lightPos-p)) dif *= .1;
    return dif;
}
//----------------------------------------------------

vec3 getColorTextura( vec3 p, vec3 nor,  int i)
{
	if (i==100 )
    { vec3 col=tex3D(iChannel0, p/32., nor); 
    return col*2.0;
    }
	if (i==101 ) { return tex3D(iChannel1, p/32., nor); }
	if (i==102 ) { return tex3D(iChannel2, p/32., nor); }
	if (i==103 ) { return tex3D(iChannel3, p/32., nor); }
}
//-------------------------------
vec3 Getluz(vec3 p, vec3 ro, vec3 rd, vec3 nor , vec3 colobj ,vec3 plight_pos)
{
    float intensity=1.0;
     vec3 result;

    result = lightingv3( nor, p, plight_pos,  rd,ro)*colobj*6.0;

    if (mObj.blnShadow==true)
    {
    	float fhadow=GetShadow(p,plight_pos);
    	return result*fhadow;

     }
    else
    {  return result; }
}

//------------------------------------------------
vec3 getMaterial( vec3 pp, float id_material)
{
  vec3 col=vec3(1.0);
  vec3 p=pp;

    if (id_material==5.0)
    {
        float escala=0.25;
    	float d = mod(floor(p.x*escala)+floor(p.z*escala*2.0),2.0);
	    return vec3( clamp(d,0.0,1.0) );
    }
}
//-------------------------------------------------

vec3 GetColorYMaterial(vec3 p,  vec3 n, vec3 ro,  vec3 rd, int id_color, float id_material)
{
   	vec3 colobj; 
    if (id_color<100)
		{ colobj=getColor(int( id_color)); }

    if ( float( id_color)>=100.0  && float( id_color)<=199.0 ) 
 	{  vec3 coltex=getColorTextura(p, n, int( id_color));
        colobj=coltex;
	}

    if (id_material>-1.0 && id_color==-1)
        { 
            colobj=vec3(0.5);
            colobj*=getMaterial(p, id_material); 
            return colobj;
        }
    return colobj;
}




vec3 renderReflect(TObj Obj, vec3 ro, vec3 rd, vec3 col )
{
   vec3 p;
   float t;
   vec3 colobj;  vec3 n;


  /////-----------REFLECT--------------
    if (int( Obj.id_color) ==201)
    {

     vec3 colref;
     for(int i=0; i<2; ++i)
      {
        t=RayMarch(ro,rd);
        Obj=mObj;

        if(t>=MAX_DIST) return col;
        if( t<0.0 || t>MAX_DIST ) break;
        p=ro+rd*t;

        n=GetNormal(p);
        //produce ondas de mar
        n=normalize(n+0.01*sin(10.0* p+2.0*iTime));

        rd=reflect(rd,n);
        ro =p+rd * MIN_DIST * 3.;

        colobj=GetColorYMaterial( p, n, ro, rd,  int( Obj.id_color), Obj.id_material)*2.0;
        colref=  Getluz( p,ro,rd, n, colobj ,light_pos1);
        colref+= Getluz( p,ro,rd, n, colobj ,light_pos2);
        colref/=1.25;
     }
     col+=colref/(float(REFLECT)+0.5);

   } 

   return col;
} 


vec3 Render(vec3 ro, vec3 rd)
{
  vec3 col = vec3(0);
   TObj Obj;
   mObj.rd=rd;
   mObj.ro=ro;

   float d = RayMarch(ro, rd);
    Obj=mObj;
    if(mObj.hitbln) 
    {
        vec3 p = (ro + rd * d ); 
        mObj.p=p;
        mObj.dist =d;
        vec3 nor=mObj.normal;

        vec3 colobj;
        colobj=GetColorYMaterial( p, nor, ro, rd,  int( Obj.id_color), Obj.id_material);

        float dif1=1.0;
        vec3 result;
        result=  Getluz( p,ro,rd, nor, colobj ,light_pos1);
        result+= Getluz( p,ro,rd, nor, colobj ,light_pos2);
        result/=1.25;
        col= result*dif1;

        //sugerencia por dean_the_coder,
        //col *= 1.0 - pow(d / 100.0, 1.5);
        col *= 1.0 - pow(d /(MAX_DIST) , 3.5);    
        col = pow(col, vec3(1.0/2.2));

    }

    col= renderReflect(Obj,  ro,  rd,  col );

   return col;
}    


///---------------------------------------------
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
   mObj.uv=uv;
    float t;
    t=mod(iTime*1.0,360.0);
    itime=t;
	//mObj.blnShadow=false;
    mObj.blnShadow=true;

 	light_pos1= vec3(5.0, 3.0, 5.0 );  light_color1 =1.25*vec3( 1.0 ,0.1,0.1);
 	light_pos2= vec3( -5.0, 6.0, -5.0 ); light_color2 =1.25* vec3( .1 ,0.1,1.0); //light_color1; //vec3( 1.0, 1.0, 1.0 );

    //vec3 ro=vec3(4.0,2.0,-25.0+t);
    vec3 ro=vec3(0.0,15.0,-20.0);
    vec3 rd=normalize(vec3(uv,1.0));
    rd= rotate_x(rd, radians(20.0));

    light_pos1+=ro;
    light_pos2+=ro;
    vec3 col= Render( ro,  rd);
    fragColor = vec4(col,1.0);
}
