// Constants
#define PI 3.14159265359
#define TAU 6.28318530718

// Utilities
#define drawSDF(dist, col) color = mix(color, col.rgb, smoothstep(unit, 0.0, dist) * col.a)

// SDFs
float sdDisk(in vec2 p, in vec2 o, in float r) {
    return length(p - o) - r;
}

float sdLine(in vec2 p, in vec2 a, in vec2 b, in float t) {
    vec2 pa = p - a, ba = b - a;
    return length(pa - ba * clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0)) - t;
}

float sdRing(in vec2 p, in vec2 o, in float r, in float t) {
    return abs(length(p - o) - r) - t;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y * 6.0;
    float unit = 12.0 / iResolution.y;
    vec3 color = vec3(1.0);

    float leftCircle = sdDisk(uv, vec2(-3.0, 0.0), 2.0);
    drawSDF(leftCircle, vec4(1.0, 0.0, 0.0, 0.5));
    drawSDF(abs(leftCircle), vec4(1.0, 0.0, 0.0, 1.0));

    float rightCircle = sdDisk(uv, vec2(3.0, 0.0), 2.0);
    drawSDF(rightCircle, vec4(0.0, 0.0, 1.0, 0.5));
    drawSDF(abs(rightCircle), vec4(0.0, 0.0, 1.0, 1.0));

    float middleCircle = sdDisk(uv, vec2(0.0), 1.0);
    drawSDF(middleCircle, vec4(0.0, 0.0, 0.0, 0.5));
    drawSDF(abs(middleCircle), vec4(0.0, 0.0, 0.0, 1.0));

    float curveUpper = max(sdDisk(uv, vec2(-1.0, 0.0), 2.0), -min(leftCircle, uv.y));
    drawSDF(curveUpper, vec4(0.0, 0.5, 0.0, 0.5));
    drawSDF(abs(curveUpper), vec4(0.0, 0.5, 0.0, 1.0));

    float curveLower = max(sdDisk(uv, vec2(1.0, 0.0), 2.0), -min(rightCircle, -uv.y));
    drawSDF(curveLower, vec4(0.0, 0.5, 0.0, 0.5));
    drawSDF(abs(curveLower), vec4(0.0, 0.5, 0.0, 1.0));

    fragColor = vec4(color, 1.0);
}
