float rand(vec2 co){
    return fract(sin(dot(co*iTime, vec2(12.9898, 78.233))) * 43758.5453);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    //vec2 uv = fragCoord/iResolution.xy;

    vec2 uv = fragCoord;

    float t = iTime * 3.;
    float k = 2.;
    float doodle = t; //(t*3.+sin(t*.75)*.5+sin(t*.33)*.25+sin(t*.25)*.125);
    //float a = .5 + .5*cos(doodle + uv.x - (uv.x*floor(uv.x/k)) + uv.y - (uv.y*floor(uv.y/k)));
    float a = cos(uv.x*.55)*(doodle)*.2 + sin(uv.y*3.14)*1.+cos(uv.y*.01); 
    a = a-floor(a);
    float b1 = (t-floor(t));
    float b = a; // + b1;
    //(uv.x*floor(uv.x / 7.0)) 
    float h = b - (b * floor(b/t));
    float h2 = (exp(h)/2.718*2.-1.);


    //vec3 midcol = vec3(1.,1.,1.);
    //vec3 cola = mix(vec3(1.,.0,1.), vec3(1.,0.,0.), h2);
    //vec3 colb = mix(vec3(0.,1.,1.), vec3(0.,.5,1.), h2);

    //vec3 cola = mix(vec3(2.,.0,.0), vec3(0.,1.,2.), h);
    //vec3 colb = mix(vec3(1.,1.,1.), vec3(0.,.0,2.), h2);
    //vec3 col = mix(cola, colb, h);

    float rval = rand(fragCoord);
    vec3 cola = mix(vec3(1.-h2,0,1.-rval), vec3(0,0,1), h);
    vec3 colb = mix(vec3(1.-h2,0.-2.,0), vec3(.5-rval,rval*h2,2.*h2+rval), h);
    vec3 col = mix(cola, colb, h);

    //vec3 cola = mix(vec3(.3,0,1), vec3(1,1,0), h);
    //vec3 colb = mix(vec3(1,.3,0), vec3(0,.5,2), h);
    //vec3 col = mix(cola, colb, (h2));


    //vec3 col = vec3(1.-h2*2., (.5-h2)*(.5-h), h2*(abs(.25-h)+1.));
    // vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    // Output to screen
    fragColor = vec4(col,1.0);
}
