void mainImage(out vec4 O, vec2 U)
{
    vec2  R = iResolution.xy,
          H = fract( 4e5 * sin( ceil(U = 5.*(U+U - R)/R.y).x * R ));

    float t = iTime * ( .5 + H.y ),
          x = U.y += 3.*H.x;
          x = floor( x-t -   .6*cos(x)  / ( 1.- .6*sin(x) )  ) + t;
    O = 1. - R.yyyy/15.* abs( length( vec2( U.y - x - .6*cos(x) -.5+.3*sin(x) , 
                                            fract( U + .1*sin(x/.1) ) -.5 )
                                    ) - mod( 8e4 * sin( H+(x-t) * R ), .2).y 
                            );
}






