const float PI = 3.14159265359;
const float s3 = sin(PI / 3.);
const float speed = 12.;

vec3 sdTriEdges(vec2 p) {
    return vec3(
        dot(p, vec2(0,-1)),
        dot(p, vec2(s3, .5)),
        dot(p, vec2(-s3, .5))
    );
}

float sdTri(vec2 p) {
    vec3 t = sdTriEdges(p);
    return max(t.x, max(t.y, t.z));
}

// Adapted from Fizzer (found through Shane in the comments of https://www.shadertoy.com/view/Wl3fD2)
// Picks the dominant axis and its sign:
// vec3(1, 2, -3) -> vec3(0, 0, -1)
vec3 primaryAxis(vec3 p) {
    vec3 a = abs(p);
//  return step(a.yzx, a.xyz)*step(a.zxy, a.xyz)*sign(p); // fails to pick a compoent when two have equal length, eg vec3(0,-2,2)
    return (1.-step(a.xyz, a.yzx))*step(a.zxy, a.xyz)*sign(p);
}

// This is the interesting bit
// Signed distance and gradient to border
vec3 sdgBorder(vec2 pt1, vec2 pt2) {

    vec3 tbRel = sdTriEdges(pt2 - pt1);    
    vec3 axis = primaryAxis(tbRel);

    vec2 gA = vec2(0,-1);
    vec2 gB = vec2(s3, .5);
    vec2 gC = vec2(-s3, .5);

    vec2 norA = gC * axis.x + gA * axis.y + gB * axis.z;
    vec2 norB = gB * -axis.x + gC * -axis.y + gA * -axis.z;

    vec2 dir = gA * axis.x + gB * axis.y + gC * axis.z;
    vec2 corner = dir * dot(dir, pt1 - pt2) * 2./3.;

    mat2 r90 = mat2(0,-1,1,0);

    bool isEdge = axis.x + axis.y + axis.z < 0.;

    if (isEdge) {
        corner = pt2 + corner;
        vec2 ca = corner + min(0., dot(corner, -norA)) * norA;
        vec2 cb = corner + max(0., dot(corner, -norB)) * norB;
        float side = step(dot(corner, dir * r90), 0.);
        corner = mix(cb, ca, side);
    } else {
        corner = pt1 - corner;
        vec2 ca = corner + max(0., dot(corner, -norA)) * norA;
        vec2 cb = corner + min(0., dot(corner, -norB)) * norB;
        float side = step(dot(corner, dir * r90), 0.);
        corner = mix(ca, cb, side);
    }

    vec2 nor = normalize(corner);

    float d = length(corner);

    return vec3(abs(d), nor);
}


// From here on is almost exactly like iq's voronoi https://shadertoy.com/view/ldl3W8

#define ANIMATE

vec2 hash2( vec2 p ) {
	return fract(sin(vec2(dot(p,vec2(127.1,311.7)),dot(p,vec2(269.5,183.3))))*43758.5453);
}

float time;

vec3 voronoi( in vec2 x, out vec2 nor )
{
    vec2 n = floor(x);
    vec2 f = fract(x);

    //----------------------------------
    // first pass: regular voronoi
    //----------------------------------
	vec2 closestCell, closestPoint;

    const int reach = 2;

    float closestDist = 8.0;
    for( int j = -reach; j <= reach; j++ )
    for( int i = -reach; i <= reach; i++ )
    {
        vec2 cell = vec2(float(i),float(j));
		vec2 o = hash2( n + cell );
		#ifdef ANIMATE
        o = 0.5 + 0.5*sin( time * PI * 2. + 6.2831*o );
        #endif	
        vec2 point = cell + o - f;
        float dist = sdTri(point);

        if( dist < closestDist )
        {
            closestDist = dist;
            closestPoint = point;
            closestCell = cell;
        }
    }


    //----------------------------------
    // second pass: distance to borders
    //----------------------------------
    closestDist = 8.0;
    for( int j = -reach-1; j <= reach+1; j++ )
    for( int i = -reach-1; i <= reach+1; i++ )
    {
        vec2 cell = closestCell + vec2(float(i),float(j));
		vec2 o = hash2( n + cell );
		#ifdef ANIMATE
        o = 0.5 + 0.5*sin( time * PI * 2. + 6.2831*o );
        #endif	
        vec2 point = cell + o - f;

        float dist = sdTri(closestPoint - point);

        if( dist > 0.00001 ) {
            vec3 sdg = sdgBorder(closestPoint, point);
            if (sdg.x < closestDist) {
                closestDist = sdg.x;
                nor = sdg.yz;
            }
        }
    }

    return vec3( closestDist, closestPoint );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord/iResolution.xx;

    time = fract(iTime / speed);

    p *= 8.;

    vec2 nor;
    vec3 c = voronoi(p, nor);

    float aa = length(fwidth(p)) * .5;

	// isolines
    float n = 15.;
    vec3 col = smoothstep(.25 - aa * n, .25 + aa * n, abs(fract(c.x * n + .25) - .5)) * vec3(1);
    // normals
    nor = mix(nor, vec2(0), smoothstep(2./n - aa, 2./n + aa, c.x));
    col = mix(col, normalize(vec3(nor, 1)) * .5 + .5, .9);
    // borders	
    col = mix( vec3(0.5,1.0,0.5) * 0., col, smoothstep( 0.01 - aa, 0.01 + aa, c.x));
    // feature points
	// float dd = length( c.yz );
	// col *= .2 + .8 * smoothstep( 0.00, 0.12, dd);
    // col = mix(col, vec3(1), smoothstep( 0.035 + aa, 0.035 - aa, dd));

	fragColor = vec4(col,1.0);
}
