mat2 Rot(float a) {
    float c = cos(a), s = sin(a);
    return mat2(c, -s, s, c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
	float f = 0.0;

	uv *= 4.0;
    float a = 3.14159 + 0.5 * thc(4., iTime);
    float c = cos(a), s = sin(a);
    mat2 rot = mat2(c, s, -s, c);

    float m = 0.5;
    float n = 6.;

    float am = 10.;
    float fr = fract(0.8 * iTime / am);
    float poop = 1.5 + floor(am * fr); // didnt know what to call this

    // (meant to use fract(0.8 * iTime) instead of fr, but I like how it looks)
    poop = mix(poop, poop + 1., fr);
    float test = poop * step(0.5, fract(0.25 + 0.5 * uv.y + 0.05 * cos( iTime + 0.2 * uv.x))) - 1.;
    for (float i = 0.; i < n; i++) {
        a = (n - i) / n * 0.006 * iTime;
        uv += 10. * a * vec2(cos(a), sin(a));
        //uv *= Rot(a);
        f += m * noise(uv); //0.5 * abs(f - m * noise( uv ));   
        uv += (5. + 0.45 * cos(uv.x * 2. + test + 0.2 * iTime)) * cos(f);
        //m *= 0.5;
    }
    //f *= 0.5;
	f = 0.5 + 0.5*f;
    f = smoothstep(0., 1., f);
    float k = 6.;
	f = smoothstep(-k, k, f);
    //f *= smoothstep( 0.0, 0.005, abs(p.x-0.6) );	

    vec3 e = vec3(1);
    vec3 col = pal(0.755 + test * f, e, e, e, 0.36 * vec3(0,1,2)/3.);
    float mx = 0.5 + 0.5 * thc(4.,0.12 * uv.x + iTime);
    col *= mix(1., test, mx);
    col = sqrt(col) + 0.05;
	fragColor = vec4(col, 1.0 );
}

#define pi 3.14159

#define thc(a,b) tanh(a*cos(b))/tanh(a)
#define ths(a,b) tanh(a*sin(b))/tanh(a)
#define sabs(x) sqrt(x*x+1e-2)

vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

float h21 (vec2 a) {
    return fract(sin(dot(a.xy, vec2(12.9898, 78.233))) * 43758.5453123);
}

float mlength(vec2 uv) {
    return max(abs(uv.x), abs(uv.y));
}

float mlength(vec3 uv) {
    return max(max(abs(uv.x), abs(uv.y)), abs(uv.z));
}

float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}


float smin(float a, float b)
{
    float k = 0.12;
    float h = clamp(0.5 + 0.5 * (b-a) / k, 0.0, 1.0);
    return mix(b, a, h) - k * h * (1.0 - h);
}
