vec3 GetDist(vec3 p  ) 
{	vec3 res= vec3(9999.0, -1.0,-1.0);  vec3 pp=p;
	float planeDist1 = p.y+0.0;  //piso inf

   p.y=p.y-10.0;
    vec3 pOrg=p;

    float dCabeza= sdEllipsoid( p-vec3(0.0,0.0,2.0), vec3(7.0,8.0,3.5));
    float dFrente= sdEllipsoid( p-vec3(0.0,2.5,0.25), vec3(5.5,3.0,2.0));
    float dPomulo= sdEllipsoid( p-vec3(0.0,-1.5,0.0), vec3(6.0,5.0,2.0));


    vec3 PosNariz=p-vec3(0.0,-1.0,0.0);
    //nariz
    vec3 aA=vec3(0.0,0.0,0.0);
    vec3 bA=vec3(0.0,3.0,0.0);
    float rA1=0.5;
    float rA2=0.25;
    vec3 p0= rotate_x( PosNariz, radians(-23.0));
    float dNariz1= sdRoundCone(p0-vec3(0.0,-2.0,-2.0), aA, bA, rA1, rA2);


    vec3 aB=vec3(0.0,0.0,0.0);
    vec3 bB=vec3(0.0,1.5,0.0);
    float rB1=0.7;
    float rB2=0.15;
    float dNariz2= sdRoundCone(p0-vec3(0.0,-2.0,-2.0), aB, bB, rB1, rB2);

    //dif nariz
    vec3 p0a= rotate_x( PosNariz-vec3(0.0,-1.2,-3.25), radians(55.0));
    float difNarizBox= sdBox( p0a, vec3(1.0,2.0,0.5) );

    //diferncia de nariz
    dNariz1=differenceSDF(dNariz1 ,difNarizBox);
    dNariz2=differenceSDF(dNariz2 ,difNarizBox);

    ///Union de cabeza
    dCabeza=opSmoothUnion(dCabeza,dFrente,0.5);
    dCabeza=opSmoothUnion(dCabeza,dPomulo,0.125);
    dCabeza=opSmoothUnion(dCabeza,dNariz1,0.5);
    dCabeza=opSmoothUnion(dCabeza,dNariz2,0.5);
     p.x = abs(p.x)-2.5; 

    vec3 p1= p-vec3(0.3,-1.8,0.0);
    float dhoyoDif=sdBox( p1, vec3(3.5,1.5,2.0) );

    float dOjo= sdEllipsoid( p, vec3(2.5,2.2,1.8)); 
    float dhoyoOjo= sdEllipsoid( p, vec3(3.,2.5,2.0));

    dhoyoOjo= differenceSDF( dhoyoOjo,dhoyoDif);
    dOjo= differenceSDF( dOjo,dhoyoDif);
    float dOjoOr=dOjo;
    dCabeza=opSmoothSubtraction(dhoyoOjo,dCabeza, 0.5 );

    //MOstrar OJo y Cabeza
    res =opU3(res, vec3(dCabeza,30,-1.0));
    res =opU3(res, vec3(dOjo,1.0,-1.0));

    ///pupila Ojo
    vec3 posMov=vec3(1.0*sin(iTime), 0.8*cos(iTime*2.0),0.0);
    vec3 posPupilaIris=vec3(0.0+posMov.x,0.5+posMov.y,0.0);
    float dPupila= sdCylinderXY(p-posPupilaIris, vec2(0.25,3.2));  //ch
    float dIris= sdCylinderXY( p-posPupilaIris, vec2(0.8,3.0)  );  //Gr
    dPupila= intersectSDF( dPupila,dOjoOr);
    dIris= intersectSDF( dIris,dOjoOr);
    res =opU3(res, vec3(dIris,2.0,-1.0));
    res =opU3(res, vec3(dPupila,0.0,-1.0));

    // oreja
    vec3 pOreja=pOrg-vec3(0.0,0.0,1.25);
    pOreja.x=abs(pOreja.x)-6.0;
    float OrejaA= sdEllipsoid( pOreja-vec3(0.0,0.0,0.0), vec3(2.0,2.0,1.5));
    float OrejaB= sdEllipsoid( pOreja-vec3(0.0,0.0,-1.0), vec3(1.0,1.2,1.5));
    OrejaA= differenceSDF(OrejaA, OrejaB);
    res =opU3(res, vec3(OrejaA,30.0,-1.0));

    //labios
    float dLabioA= sdEllipsoid( pOrg-vec3(0.0,-3.5,-1.1), vec3(2.0,0.5,1.0));
    float dLabioB= sdEllipsoid( pOrg-vec3(0.0,-4.0,-1.0), vec3(2.0,0.5,1.0));
    res =opU3(res, vec3(dLabioA,2.0,-1.0));
    res =opU3(res, vec3(dLabioB,2.0,-1.0));

    //cejas
    vec3 posCejas=pOrg.yzx-vec3(1.2,-1.5,0.0);
    posCejas.z= abs(posCejas.z)-2.5;
    float dcejasA=sdTorus(posCejas, vec2(2.0,0.5) );
    vec3 posCejasBx=pOrg-vec3(0.0,0.5,0.0);
    posCejasBx.x=abs(posCejasBx.x)-2.5;
    float dCejasDif=sdBox(posCejasBx, vec3(3.0,1.8,2.5) );
    dcejasA= differenceSDF(dcejasA, dCejasDif);
    res =opU3(res, vec3(dcejasA,0.0,-1.0));

    return res;
}

vec3 GetNormal(vec3 p)
{   float d = GetDist(p).x;
    vec2 e = vec2(.001, 0);
    vec3 n = d - vec3(GetDist(p-e.xyy).x,GetDist(p-e.yxy).x,GetDist(p-e.yyx).x);
    return normalize(n);
}

float RayMarch(vec3 ro, vec3 rd, int PMaxSteps)
{   float t = 0.; 
    vec3 dS=vec3(9999.0,-1.0,-1.0);
    float marchCount = 0.0;
    vec3 p;

    #define DISTANCE_BIAS 0.75
    float minDist = 9999.0; 

    for(int i=0; i <= PMaxSteps; i++) 
    {  	p = ro + rd*t;
        dS = GetDist(p);
        t += dS.x;
        if ( abs(dS.x)<MIN_DIST  || i == PMaxSteps)
            {mObj.hitbln = true; minDist = abs(t); break;}
        if(t>MAX_DIST)
            {mObj.hitbln = false;    minDist = t;    break; } 
        marchCount++;
    }
    mObj.dist = minDist;
    mObj.id_color = dS.y;
    mObj.marchCount=marchCount;
    mObj.id_material=dS.z;
    mObj.normal=GetNormal(p);
    mObj.phit=p;
    return t;
}

float GetShadow(vec3 p, vec3 plig)
{   vec3 lightPos = plig;
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);
    float dif = clamp(dot(n, l), 0., 1.);
    float d = RayMarch(p+n*MIN_DIST*2., l , MAX_STEPS/2);
    if(d<length(lightPos-p)) dif *= .1;
    return dif;
}

vec3 amb(vec3 c, float k)
{  return c * k; }

float diff(vec3 p,vec3 lp,vec3 n )
{   vec3 l = normalize(lp - p);
    float dif = clamp(dot(l, n), 0.0, 1.0);
    return dif;
}
float spec( vec3 p, vec3 lp,vec3 rd,vec3 n )
{ vec3 l = normalize(lp - p);
  vec3 r = reflect(-l, n);
  float spe =pow(clamp(dot(r, -rd), 0.0, 1.0), 20.0);
  return spe;
}

vec3 lightingv3(vec3 normal,vec3 p, vec3 lp, vec3 rd, vec3 ro,vec3 col, float dis) 
{   vec3 l = normalize(lp - p);
    vec3 ldir = normalize(p-rd);    
    float distA = max(length(l), 0.01);
    float distB = 1.0/(length(p-lp));
    float dist=(distA+distB)/2.0;
    float atten = min(1./(1. + dist*0.5), 0.2);
    l /= (dist);

    vec3 n = normal;
   	vec3 r = reflect(-l, n);

    vec3 amb=amb(col, 0.5);
    float dif = diff( p, lp, n );
    //float diff=max(0.,dot(ldir,-n));
    float diff=max(dot(normalize(p-lp), -n), 0.0);;
    vec3 ln=normalize(lp);

    float spe= spec(  p,  lp, rd, n );
    float fre = pow(clamp(1.0 + dot(n, rd), 0.0, 1.0), 2.0);
    float dom = smoothstep(-1.0, 1.0, r.y);
     float bac=clamp(dot(n,-l),0.0,1.0);
    float rim=pow(1.0+dot(n,rd),3.0);
    float dn=.15*max(0.,dot(normalize(rd),-n));

     vec3  hal = normalize(-rd+l);
     float dif3 = clamp( dot(n,l), 0.0, 1.0 );
     float amb2 = clamp( 0.5 + 0.5*dot(n,vec3(0.0,1.0,0.0)), 0.0, 1.0 );
     float occ = 0.5 + 0.5*n.y;

    float fshadow;
    float sh = clamp(dot(n,normalize(lp)),0.0,1.0);

    if (mObj.blnShadow==true) {fshadow=GetShadow(p,lp);}
    else {fshadow=0.5;}

    vec3 lin=vec3(1.0);
    lin*= amb*amb2*occ;
    lin += 1.0*(dif+diff+dif3)*sh;
    lin += 2.5*spe*vec3(1.0);
    lin += 2.5*fre*vec3(1.);
    lin *= atten*0.5*col*fshadow;
    lin *= vec3(1.0)*  max(normalize(vec3(length(lin))).z, 0.)+ .75; 
    lin = pow(lin,vec3(0.4545));

    return lin;
    //return lin*atten*(1.0/2.0);

}

vec3 Getluz(vec3 p, vec3 ro, vec3 rd, vec3 nor , vec3 colobj ,vec3 plight_pos, float dis)
{  float intensity=1.0;
     vec3 result;
