float noise (vec2 st, float maxx, float comp, float modx) 
{
    float xtr = mod(floor(st.x/comp)*modx, 271.);
    float rand = sin(mod(xtr*xtr, 311.))*comp*2.0;
    return float(st.y < float(int(floor(rand)) % int(maxx)));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   

    // change value from 0.0 to 1.0
    // most optimal - 0.8-1.0
    float quality = 1.;
    // optimal - 0.0-5.0
    float light = 3.;

    vec2 uv = fragCoord;
    vec3 res = iResolution;

    //uncomment for a mirror effect
    //uv.y = res.y-abs(uv.y-(res.y/2.))*2.;

    vec3 color = vec3(0,0,0);
    float pix = 0.;

    float f = res.x/50.;
    float lay = 1.;
    float q = 2.-quality+0.01;
    vec2 l1 = uv;

    for (float i=0.01; i<=1.; i*=q){
        lay += float(q*1e2-1e2);
        l1.x = uv.x + (iTime+floor(lay/10.)*50.)*(f*i*10.) - res.x/2.;
        l1.y = uv.y - (res.y+f)*(1.-i)/1.2-res.y/8.;
        pix = max(pix, noise(l1, res.y*i, 5.*i*f, 5.*f)*i*(uv.y/res.y*light)); 
    }

    vec2 uvk = fragCoord/res.xy;
    vec3 col = 0.5 + 0.5*cos(iTime+uvk.xyx+vec3(0,2,4));

    color = vec3(pix);



    fragColor = vec4(vec3(color*col),1.);

}
