const float PI = 3.1415962;

float line(vec2 p,vec2 a,vec2 b){
  return length(clamp(dot(b-=a,p-=a)/dot(b,b),0.,1.)*b-p);
}

vec2 rot90(vec2 a){return vec2(-a.y,a.x);}

float shape0(vec2 p,vec2 d){
    p=abs(p);
    vec2 a = vec2(1,0);
    vec2 b = vec2(1,d.y);
    vec2 c = vec2(0,d.y+d.x);
    return sign(max(dot(p-b,rot90(a-b)),dot(p-c,rot90(b-c))))*
    min(
        line(p,a,b),
        line(p,b,c)
    );
}

float SuperS(vec2 p,vec2 a){
    p*=step(0.,p.y)*2.-1.;
    p.y=p.y-a.y-a.x*.5;
    float d = shape0(p,a);
    d = max(d,-line(p,vec2(0,a.y),vec2(0,-a.y)));
    d = max(d,-line(p,vec2(0,-a.y),vec2(1,-a.y-a.x)));
    return d;
}

void mainImage(out vec4 O, vec2 U){
    float z = 7.;
    vec2 uv = (2.*U - iResolution.xy)/iResolution.y*z;
    vec2 j = floor(uv*.4);
    uv.x = (uv.x*.4-j.x-.5)/.4;
    vec2 m = (2.*iMouse.xy - iResolution.xy)/iResolution.y;
    float AA = .5/z*iResolution.y;

    float d = SuperS(uv,vec2(cos(iTime*1.73+j.x)+1.,cos(iTime+j.x)*.3+.7));

    O = vec4(1);
    O.xyz *= min(1.,abs(d)*AA-AA*.05);

    if(iMouse.z>0.) 
        O.xyz *= .8 + .4*cos(d*PI*10.);

    O.xyz *= mix(vec3(0.000,0.357,0.522),vec3(1,sin(j.x)*.5+.5,.3),step(d,0.));

    //O = vec4(abs(d));
    //O.xyz=1.-exp(-O.xyz);
    O.xyz=sqrt(O.xyz);
}
