#define S(v) smoothstep(90./R.y,0.,  v )
void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = 1.5*( u+u - R ) / R.y;
 // O-=O; 
    float c = 0., z = -9., i,x,y;
    for( int k=0; k<3; k++ ) {          // angled layers
        if ( abs(U.y) < 1. )
            for( i = 0.; i<6.; i++ ) {  // loop required because of neighbors superimposition   
             // y = sin(10.*U.y)*.8*sin(i-1.) , x = 20.*U.x - i+3. + y; // variant
                y = sin(10.*U.y) , x = 20.*U.x - i+3. + y*.8*sin(i-1.);
                if (abs(x) < 20. ) 
                    x = abs( mod(x,6.) -.5) - .2,
                 // O.ra +=  (1.-O.a)                 // simple blending
                 //          * vec2( (.2+.8*S(abs(x))), 1 )
                 //          * S( x ), 
                    y > z && S(x) > 0. ?              // Z-buffer
                        c = (.2+.8*S(abs(x))) * S(x), // ribbon pattern * mask
                        z = y : y;
            } 
        U *= mat2(cos(vec4(1,12,34,1)));              // ~ rot pi/3
    }

    O = sqrt(  c *vec4(.7,.7,1,1) );
}
