#define antialiasing(n) n/min(iResolution.y,iResolution.x)
#define S(d,b) smoothstep(antialiasing(1.0),b,d)
#define Rot(a)mat2(cos(a),-sin(a),sin(a),cos(a))
#define B(p,s) max(abs(p).x-s.x,abs(p).y-s.y)
#define DF(a,b) length(a) * cos( mod( atan(a.y,a.x)+6.28/(b*8.0), 6.28/((b*8.0)*0.5))+(b-1.)*6.28/(b*8.0) + vec2(0,11) )

// Using the Desmos website to create the following formula
float flowerleaf(vec2 p, float w, float h){
    p.y-=h;
    p.x = abs(p.x);
    float a = (p.x*0.5)-(-(p.x*p.x)-p.y*w);
    //float b = -(p.x*0.5)-(-(p.x*p.x)-p.y*w);
    float d = a;
    d = max(-p.y-(h*2.0),d);
    return d;
}

vec3 flower(vec2 p, vec3 col){
    p*=2.0;
    p.y-=0.15;
    vec2 prevP = p;

    p.x+=sin(p.y*12.0)*0.2*cos(p.x*7.0)*0.5;
    float d = B(p-vec2(0.0,-0.5),vec2(0.03,0.3));
    col = mix(col,vec3(0.3,0.4,0.3),S(d,0.0));

    p = prevP;
    p*=Rot(radians(iTime*-20.0));
    p = DF(p,4.0);
    p = abs(p);
    p -= vec2(0.23);

    d = flowerleaf(p*Rot(radians(45.0)),0.6,0.2);
    col = mix(col,vec3(0.3,0.0,0.0),S(d,0.0));

    p = prevP;

    p*=Rot(radians(iTime*25.0));
    p = DF(p,2.5);
    p = abs(p);
    p -= vec2(0.18);

    d = flowerleaf(p*Rot(radians(45.0)),0.6,0.2);
    col = mix(col,vec3(0.6,0.0,0.0),S(d,0.0));

    p = prevP;

    p*=Rot(radians(iTime*-30.0));
    p = DF(p,1.5);
    p = abs(p);
    p -= vec2(0.1);

    d = flowerleaf(p*Rot(radians(45.0)),0.5,0.2);
    col = mix(col,vec3(0.8,0.1,0.1),S(d,0.0));

    p = prevP;

    p*=Rot(radians(iTime*35.0));
    p = DF(p,2.0);
    p = abs(p);
    p -= vec2(0.07);


    d = flowerleaf(p*Rot(radians(45.0)),0.5,0.1);
    col = mix(col,vec3(0.9,0.3,0.3),S(d,0.0));


    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    p.y-=iTime*0.1;
    p*=2.;
    vec3 col = vec3(0.0);

    p = mod(p,0.7)-0.35;

    col = flower(p,col);

    fragColor = vec4(col,1.0);
}
