//           P = intersec, F = polar grid cells -inward offset, then local cells coords 
#define A(s) P = ( s sqrt(d) - b ) * D/z, P.z += 5.,                                    \
             F = round( vec2( atan(P.x,P.z) -t, asin(P.y/r) ) * V ) / V, F.x += t,      \
             abs(F.y) < 1. ? Z = ( 5.-r*cos(F.y)*cos(F.x) ),                            \
                             F-= .1*P.xy, Z -= .1*P.z,                                  \
                             F = r* vec2( cos(F.y)*sin(F.x) , sin(F.y) ) / Z + U/z : F, \
             O +=  3e-6/(Z-3.7) / dot(F,F)
//                               r* vec2( cos(F.y)*sin(F.x) , sin(F.y) ) - P.xy : F, \

//#define S   d > 0. ? A(), A(-) : O // buggy on some Windows
  #define S   if (d > 0.) A(), A(-)


void mainImage( out vec4 O, vec2 U )
{
    vec2  R = iResolution.xy, F, V = vec2(6.37,5.09); // vec2(20,16) / 3.14
    vec3  D = vec3( U = 1.2 * ( U+U - R ) / R.y, 5 ), P;
    O -= O;
    float z = length(D), Z, r = 1., t = -.4*iTime,
          b = D.z/z*5., d = b*b-24.;  // ray inter sph: l² + 2(D.C).l + C² = r²
    S;                                // outer sphere
    r = .7, d -= .5; t=-t;  S;        // inner sphere
}
