// if defined, adjusts the latitude so that equal spaced points on the map
// are equal spaced on the sphere; if not defined, the latitude will map
// exactly to the radius.
#define FIX_EQUAL_AREA

// if defined, adjusts the latitude so that crossing angles are preserved
// this excludes the equidistant or equal-area properties.
//#define FIX_CONFORMAL

// if defined, stretches the map to cover the entire UV square; various
// modes are available below, of which the default provides an equiareal
// mapping.
//#define SQUARIFY

// if defined, shows an environment map instead of a XYZ color gradient
// for inverse mode, demonstrates texture mapping
#define SHOW_CUBEMAP

// if defined, shows latitude/longitude lines
#define SHOW_FLOW_LINES

// if defined, shows the latitude as cos(theta) instead of theta
//#define SHOW_COS_THETA

// if defined, shows a UV tesselation of quadliterals, and the proportional
// difference between the quadliteral area and the expected average size (2/N²)
// if FIX_EQUAL_AREA is defined, the proportions will be ideal.
//#define SHOW_SOLID_ANGLE

// if not defined, computes a more accurate solid angle from cosines,
// though the precision of this method is terrible and is only relevant
// for rough tesselations anyway.
#define USE_TRIANGLE_AREA

// subdivisions per quadrant
#if defined(SHOW_SOLID_ANGLE)||defined(SHOW_INVERSE)
#undef SHOW_FLOW_LINES
// solid angle subdivisions
const float subdivisions = 32.0;
#else
// flow line subdivisions
const float subdivisions = 16.0;
#endif

// thesis related functions
//////////////////////////////////////////////////////////

//#define TEST_EQUIAREAL
//#define TEST_INVERTIBLE
#ifdef TEST_INVERTIBLE
#undef SHOW_CUBEMAP
#endif

// some of the square<->disc conversion functions are from
// https://arxiv.org/abs/1509.06344
// https://jcgt.org/published/0005/02/01/
// area normalization after
// Stratified Sampling of 2-Manifolds, James Arvo
// https://citeseerx.ist.psu.edu/viewdoc/download?doi=10.1.1.88.3412&rep=rep1&type=pdf

// approximate inverse of f(x) = x - sin(x)
// used by chord mapping
float inverse_diff_sinx_x(float y) {
    // newton's method
    float y0 = mod(y, radians(360.0));
    float x = y0 - sign(y0 - radians(180.0));
    // higher resolutions might require more iterations
    for (int i = 0; i < 4; ++i) { 
        float f_x = x - sin(x) - y0;
        float ff_x = 1.0 - cos(x);
        x = x - f_x / ff_x;
    }
    return (y == 0.0)?y:(x + y - y0);
}

vec2 inv_shirley_chiu(vec2 p) {
    // Shirley-Chiu Mapping (L1 arclength, equiareal)
    float r = length(p);
    float a = atan(p.y, p.x) / radians(360.0) - 0.125;
    float px = abs(fract(a) - 0.5);
    float py = abs(fract(a - 0.25) - 0.5);
    return (4.0 * r) * vec2(px - py, px + py - 0.5);
}

vec2 shirley_chiu(vec2 p) {
    // Shirley-Chiu Mapping (L1 arclength, equiareal)
    bool is_h = (abs(p.x) > abs(p.y));
    vec2 o = is_h?p:p.yx;
    float r = o.x;
    float h = o.y/r - 1.0;
    float phi = radians(45.0)*(1.0 + ((r == 0.0)?0.0:(is_h?h:-h)));
    return vec2(cos(phi),sin(phi))*r;
}

// map a square [-1..1]x[-1..1] to a disc of radius 1
vec2 square_disc(vec2 p) {
#if 0
    // Elliptical Grid Mapping (Philip Nowell)
    // http://mathproofs.blogspot.com/2005/07/mapping-square-to-circle.html
    p = p*sqrt(1.0 - p.yx*p.yx/2.0);
    return p;
#elif 0
    // FG-Squircular Mapping (Fernandez Guasti squircle)
    vec2 pp = p*p;
    float L = pp.x + pp.y;
    p = p*sqrt((L - pp.x * pp.y)/L);    
    return p;
#elif 0
    // polar equi-areal
    float r = sqrt((p.x+1.0)/2.0);
    float phi = p.y*radians(180.0);
    return r*vec2(cos(phi),sin(phi));
#elif 0
    // chord mapping, equi-areal
    float a = (1.0 - abs(p.y))*radians(180.0); // A*2
    float theta = inverse_diff_sinx_x(a)/2.0; // theta/2
    p.y = cos(theta)*sign(p.y);
    p.x = p.x * sin(theta);
    return p;
#elif 0
    // half-concentric chord mapping, equi-areal
    p = vec2(p.y + p.x, p.x - p.y)*0.5;

    float h1 = 1.0 - abs(p.y);
    p.x = (h1 == 0.0)?0.0:(p.x / h1);

    float a = h1*h1*radians(180.0); // A*2
    float theta = inverse_diff_sinx_x(a)/2.0; // theta/2
    p.y = cos(theta)*sign(p.y);
    p.x = p.x * sin(theta);

    p = vec2(p.y + p.x, p.x - p.y)*sqrt(0.5);
    return p;
#elif 0
    // L1 elliptical grid mapping, area-normalized
    vec2 s = sign(p);
    p = abs(p);

    // warp parameters
    float c0 = 1.0/2.0*sqrt(-8.0*p.x + 9.0);
    p = vec2(
        3.0/2.0 - c0,
        1.0/2.0 + c0 - sqrt(5.0/2.0 - 2.0*p.x - c0*(2.0*p.y - 1.0)));
    // map
    p = p*(1.0 - p.yx/2.0);

    p = p * s;

    p = vec2(p.y + p.x, p.y - p.x);
    p = shirley_chiu(p);    
    return p;
#elif 1
    // Shirley-Chiu Mapping (L1 arclength, equiareal)
    return shirley_chiu(p);
#else
    // Stretching
    return p/length(p)*max(abs(p.x),abs(p.y));
#endif
}

// map a disc of radius 1 to a square [-1..1]x[-1..1]
vec2 disc_square(vec2 p) {
#if 0
    // Elliptical Grid Mapping (Philip Nowell)
    // http://mathproofs.blogspot.com/2005/07/mapping-square-to-circle.html
    vec2 pp = p*p;
    float z1 = pp.x - pp.y;
    vec2 z = 2.0 + vec2(z1,-z1);
    vec2 w = 2.0 * sqrt(2.0) * p;
    return (sqrt(z + w) - sqrt(z - w))/2.0;
#elif 0
    // FG-Squircular Mapping (Fernandez Guasti squircle)
    vec2 pp = p*p;
    float L = pp.x + pp.y;
    return sqrt(0.5)*sign(p)/abs(p.yx)* sqrt(L - sqrt(L*(L - 4.0*pp.x*pp.y)));
#elif 0
    // polar equi-areal
    float r = dot(p,p);
    float phi = atan(p.y,p.x);
    return vec2(r*2.0-1.0, phi/radians(180.0));
#elif 0
    // chord mapping, equi-areal
    float c = sqrt(1.0 - p.y*p.y); // half chord
    float a = asin(c) - c*abs(p.y);
    p.x /= c;
    p.y = (1.0 - a/radians(90.0))*sign(p.y);
    return p;
#elif 0
    // half-concentric chord mapping, equi-areal
    p = vec2(p.y + p.x, p.x - p.y)*sqrt(0.5);
    float c = sqrt(1.0 - p.y*p.y);
    float a = asin(c) - c*abs(p.y);
    float h1 = sqrt(a/radians(90.0));
    p.y = (1.0 - h1)*sign(p.y);
    p.x = (c == 0.0)?0.0:(p.x * h1 / c);
    p = vec2(p.y + p.x, p.x - p.y);    
    return p;
#elif 0
    // L1 elliptical grid mapping, area-normalized
    p = inv_shirley_chiu(p);
    p = vec2(p.x - p.y, p.x + p.y)/2.0;
    vec2 s = sign(p);
    p = abs(p);
    p = 1.0/2.0*p - 1.0/2.0*p.yx - 1.0/2.0*sqrt(p*p - 2.0*(p + 2.0)*p.yx + p.yx*p.yx - 4.0*p + 4.0) + 1.0;
    p = vec2(
        3.0/2.0*p.x - 1.0/2.0*p.x*p.x,
        (2.0*(p.x - 2.0)*p.y + p.y*p.y)/(2.0*p.x - 3.0));    
    p = p * s;
    return p;
#elif 1
    // Shirley-Chiu Mapping (L1 arclength, equiareal)
    return inv_shirley_chiu(p);
#else
    // Stretching
    return p/max(abs(p.x),abs(p.y))*length(p);
#endif
}

vec3 conformal_normal(vec2 uv) {
    // Stereographic map projection
    float rr = dot(uv,uv);
    float sin_a_div_r = 2.0/(rr + 1.0);
    float cos_a = (1.0 - rr)*0.5*sin_a_div_r;
    return vec3(uv*sin_a_div_r, cos_a);
}

vec2 conformal_disc(vec3 n) {
    // Stereographic map projection
    return n.xy/(n.z + 1.0);
}

vec3 equalarea_normal(vec2 uv) {
    // lambert azimuthal equal-area projection
    float rr = dot(uv,uv);
    float cos_a = 1.0 - rr;
    float sin_a_div_r = sqrt(2.0 - rr);
    return vec3(uv*sin_a_div_r, cos_a);
}

vec2 equalarea_disc(vec3 n) {
    // lambert azimuthal equal-area projection
    return n.xy/sqrt(n.z + 1.0);
}

vec3 equidistant_normal(vec2 uv) {
    // azimuthal equidistant projection
    float r = length(uv);
    float a = r*radians(90.0);
    return vec3(((r==0.0)?vec2(0.0):(uv/r*sin(a))), cos(a));
}

vec2 equidistant_disc(vec3 n) {
    // azimuthal equidistant projection
    float a = acos(n.z);
    float r = a/radians(90.0);
    return n.xy/sin(a)*r;
}

// arcmap coordinates to normal
vec3 arcmap_normal(vec2 uv) {
#ifdef SQUARIFY
    uv = square_disc(uv);
#endif
#ifdef FIX_CONFORMAL
    return conformal_normal(uv);
#elif defined(FIX_EQUAL_AREA)
    return equalarea_normal(uv);
#else
    return equidistant_normal(uv);
#endif        
}

// normal to arcmap coordinates
vec2 normal_arcmap(vec3 n) {
    vec2 uv;
#ifdef FIX_CONFORMAL
    uv = conformal_disc(n);
#elif defined(FIX_EQUAL_AREA)
    uv = equalarea_disc(n);
#else
    uv = equidistant_disc(n);
#endif  
#ifdef SQUARIFY
    return disc_square(uv);
#else
    return uv;
#endif
}

// visualization
//////////////////////////////////////////////////////////

float triangle_area(vec3 A, vec3 B, vec3 C) {
    return length(cross(B - A, C - A)) / 2.0;
}

vec2 nanglebasis (vec3 a, vec3 b) {
    float ab = dot(a, b);
    return vec2(ab, sqrt(1.0 - ab * ab));
}

float simplex_solid_angle (vec2 u, vec2 v, vec2 w) {
    float A =
            acos((u.x - v.x * w.x) / (v.y * w.y))
            + acos((v.x - w.x * u.x) / (w.y * u.y))
            + acos((w.x - u.x * v.x) / (u.y * v.y))
            - radians(180.0);
    return (A != A)?0.0:A;
}

float pyramid_points_solid_angle (vec3 A, vec3 B, vec3 C, vec3 D) {
#ifdef USE_TRIANGLE_AREA
    return triangle_area(A, B, C) + triangle_area(C, D, A);
#else
    vec2 ab = nanglebasis(A, B);
    vec2 ac = nanglebasis(A, C);
    vec2 da = nanglebasis(A, D);
    vec2 bc = nanglebasis(B, C);
    vec2 cd = nanglebasis(C, D);
    return simplex_solid_angle(bc, ac, ab) + simplex_solid_angle(da, ac, cd);
#endif
}

vec3 plasma(float t) {

    const vec3 c0 = vec3(0.05873234392399702, 0.02333670892565664, 0.5433401826748754);
    const vec3 c1 = vec3(2.176514634195958, 0.2383834171260182, 0.7539604599784036);
    const vec3 c2 = vec3(-2.689460476458034, -7.455851135738909, 3.110799939717086);
    const vec3 c3 = vec3(6.130348345893603, 42.3461881477227, -28.51885465332158);
    const vec3 c4 = vec3(-11.10743619062271, -82.66631109428045, 60.13984767418263);
    const vec3 c5 = vec3(10.02306557647065, 71.41361770095349, -54.07218655560067);
    const vec3 c6 = vec3(-3.658713842777788, -22.93153465461149, 18.19190778539828);

    return c0+t*(c1+t*(c2+t*(c3+t*(c4+t*(c5+t*c6)))));
}

vec2 rotate(vec2 p, float a) {
    float c = cos(a); float s = sin(a);
    return vec2(p.x*c + p.y*s, s*p.x - c*p.y);
}

vec3 stereographic_sphere(vec2 uv) {
    vec3 n = vec3(uv, sqrt(1.0 - dot(uv,uv)));
    float t = iTime*0.1;
    n.yz = rotate(n.yz, radians(sin(t*0.25*radians(360.0))*45.0));    
    n.xz = rotate(n.xz, t*radians(360.0));    
    return n;
}

//////////////////////////////////////////////////////////

// for inverse mapping
vec3 transfer_color(vec2 q) {
    q = q*0.5 + 0.5;
#ifdef SHOW_CUBEMAP
    return textureLod(iChannel1, q*2.0, 0.0).rgb;
#elif 1
    ivec2 p = ivec2(q*subdivisions) & 1;
    return vec3(p.x^p.y);
#else
    q = q - mod(q, 1.0/subdivisions);
    return vec3(q, 0.0);
#endif
}

vec3 transfer_color(vec3 p) {
#ifdef SHOW_CUBEMAP
#ifndef TEST_EQUIAREAL
    p.xz = rotate(p.xz, iTime*0.5);
#endif
    return textureLod(iChannel0, p, 0.0).xyz;
#elif defined(SHOW_INVERSE)
    vec2 q = normal_arcmap(p);
    q = q - mod(q, 1.0/subdivisions);
    return vec3(q*0.5 + 0.5, 0.0);
#else
    return p*0.5 + 0.5;
#endif
}

float flow_lines(vec3 q) {
#ifndef TEST_EQUIAREAL
    q.xz = rotate(q.xz, radians(90.0) + iTime*0.5);
#endif
    float a = atan(q.y, q.x);
#ifdef SHOW_COS_THETA
    float r = q.z*radians(90.0);
#else
    float r = acos(q.z);
#endif
    float A = subdivisions;
    return sin(a*A)*sin(r*A);
}

float arcmap_flow_lines(vec2 p) {
    return flow_lines(arcmap_normal(p));
}

bool in_range(vec2 p) {
#ifdef SQUARIFY
    return max(abs(p.x),abs(p.y)) <= 1.0;
#else
    return length(p) <= 1.0;
#endif
}

vec3 solid_angle_color(vec3 p00, vec3 p10, vec3 p01, vec3 p11) {
#ifdef SQUARIFY    
    // ideal solid angle: pi / (2*subdivisions²)
    float isa = radians(180.0) / (2.0 * (subdivisions * subdivisions));
#else
    // ideal solid angle: 2 / (subdivisions²)
    float isa = 2.0 / (subdivisions * subdivisions);
#endif
    float sa1 = pyramid_points_solid_angle(p00,p10,p11,p01);
    float sa2 = pyramid_points_solid_angle(p10,p00,p01,p11);
    float sa = (uv.x*uv.y < 0.0)?sa1:sa2;
#if 1
    sa = abs(sa/isa-1.0);
#else
    sa = abs(log2(sa/isa));
#endif
    return plasma(clamp(sa,0.0,1.0));
}

void paint_arcmap() {
    set_line_width_px(1.2);

    vec2 uv = get_origin();
    #ifdef TEST_INVERTIBLE
    vec2 p = normal_arcmap(arcmap_normal(uv));
    #else
    vec3 p = arcmap_normal(uv);
    #endif
    set_source_rgb(transfer_color(p));
#ifdef SHOW_SOLID_ANGLE
    float s = 1.0/subdivisions;
    uv = uv - mod(uv, s) + s * 0.5;
    vec2 w = 0.5*vec2(-s,s);
    vec3 p00 = arcmap_normal(uv + w.xx);
    vec3 p10 = arcmap_normal(uv + w.yx);
    vec3 p01 = arcmap_normal(uv + w.xy);
    vec3 p11 = arcmap_normal(uv + w.yy);
    set_source_rgb(solid_angle_color(p00,p10,p01,p11));
#endif
#ifdef SQUARIFY
    rectangle(-1.0,-1.0,2.0,2.0);
#else
    circle(0.0, 0.0, 1.0);
#endif
    fill_preserve();    
    set_source_rgb(vec3(0.0));
    stroke();
#ifdef SHOW_FLOW_LINES
    if (in_range(get_origin())) {
        graph2D(arcmap_flow_lines);
        stroke();
    }
#endif
}

void paint_inverse_arcmap() {
    set_line_width_px(1.2);
    vec3 n = stereographic_sphere(get_origin());  
    float z = n.z;
    n.z = abs(n.z);
    vec2 uv = normal_arcmap(n);  
    if (z < 0.0) {
        uv.x = -uv.x;
    }
    set_source_rgb(transfer_color(uv));
    circle(0.0, 0.0, 1.0);
    fill_preserve();
    set_source_rgb(vec3(0.0));
    stroke();
}

void paint() {
    set_source_rgb(vec3(0.2));
    clear();

#if defined(SHOW_INVERSE)
    paint_inverse_arcmap();
#else
    paint_arcmap();
#endif    
}

//////////////////////////////////////////////////////////

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    init(fragCoord, iMouse.xy, iResolution.xy);

    paint();

    blit(fragColor);
}

#ifdef GLSLSANDBOX
void main() {
    mainImage(gl_FragColor, gl_FragCoord.xy);
}
#endif

