// "Circuit Diagram2" by ntsutae (modified by jarble)
// https://twitter.com/ntsutae/status/1268820823952916486
// https://www.openprocessing.org/sketch/912094

//mutated by derSchamane 2022 = Ancient Circuit Diagram =

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  float t = iTime;
  int x = int(fragCoord.x - iResolution/2.),
      y = int(fragCoord.y +30.*t + 10000.),
      r = (x+y+1)^(x-y);
  float k = smoothstep(.5, 1., cos(t/5.+float(r)/(1000.+sin(t/97.)*100.)))-sin(t/8.+float(r)/200.)*.2;
  float b = smoothstep(k*1., 1.+k*3., float(abs(r*r*r/(y+x+int(t*49.))) % (9970))/1000.);
  fragColor = vec4(vec3((1.1-sin(t/9.)*.2-k)-b*b, (.8-k)-b, b*.3+k*.2), 1.);
}
