vec2 square_complex(vec2 c)
{
    // (a + bi)*(a + bi)
    // a*a+abi + bia+bibi
    // a*a+abi + abi+bibi
    // a*a+2abi+bibi
    // a*a+2abi+bbii
    // a*a+2abi-b*b
    // aa-bb+2abi
    return vec2(c.x*c.x - c.y*c.y, 2.0*c.x*c.y);
}

int iterations = 20;

struct MandelResult
{
    int it;
    float dist;
};

MandelResult mandelbrot_point(vec2 c)
{
    vec2 z = vec2(0, 0);
    int it = 0;
    while (it < iterations && dot(z, z) < 4.0)
    {
        z = square_complex(z) + c;
        it++;
    }
    return MandelResult(it, dot(z, z) - 4.0);
}

vec3 color_from_point(vec2 point)
{
    MandelResult it = mandelbrot_point(point);
    float fit = (float(it.it)) / float(iterations);
    return vec3(fit, fit, it.dist);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= vec2(0.5, 0.5);
    uv *= 2.0;
    uv.y *= iResolution.y/iResolution.x;

    uv *= 2.2;

    // Getting final value from uv as position
    vec3 col = color_from_point(uv);

    // Output to screen
    fragColor = vec4(col,1.0);
}
