bool rect_contains_point(vec2 p1, vec2 p2, vec2 p) {
    return p1.x <= p.x && p.x <= p2.x &&
           p1.y <= p.y && p.y <= p2.y;
}

bool circle_contains_point(vec2 c, float r, vec2 p) {
    return length(c - p) <= r;
}

vec2 closest_point(vec2 p1, vec2 p2, vec2 c) {
    float x = max(p1.x, min(p2.x, c.x));
    float y = max(p1.y, min(p2.y, c.y));
    return vec2(x, y);
}

#define WIDTH 1600.0
#define HEIGHT 900.0

vec2 remap(vec2 p)
{
    return p/iResolution.xy*vec2(WIDTH, HEIGHT);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = remap(fragCoord.xy);

    vec2 size = vec2(150.0*3.0, 100.0*3.0);
    vec2 p1 = vec2(WIDTH*0.5-size.x*0.5, HEIGHT*0.5-size.y*0.5);
    vec2 p2 = p1 + size;

    vec2 c = remap(iMouse.xy);
    float r = 100.0;

    vec2 cp = closest_point(p1, p2, c);

    fragColor = vec4(0.0);

    if (rect_contains_point(p1, p2, p)) {
        fragColor += vec4(.0, .0, 1.0, 0.0);
    }

    if (circle_contains_point(c, r, p)) {
        if (length(c - cp) <= r) {
            fragColor += vec4(1.0, 0.0, 0.0, 0.0);
        } else {
            fragColor += vec4(.0, 1.0, 0.0, 0.0);
        }
    }

    if (circle_contains_point(cp, 10.0, p)) {
        fragColor += vec4(1.0);
    }
}
