mat2 rotate2D(in float a) {
    float co = cos(a), si = sin(a);
    return mat2(co, -si, si, co);
}

float sdBox(in vec2 p, in vec2 size) {
    p = abs(p) - size;
    return length(max(p, 0.0)) + min(0.0, max(p.x, p.y)); // + min(0.0, max(p.x,p.y) to corrent
                                                        // inretior distance which would otherwise
                                                        // be zero
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.y;
    float aaWidth = 8.0 / iResolution.y; // Pixel width in normalized coordinates (x2 to make it smoother);

    vec3 color = vec3(1.0);

    uv *= rotate2D(iTime);

    float box = sdBox(uv, vec2(0.3, 0.3));
    box = max(box, -sdBox(uv, vec2(0.15, 0.1))); // "carve" out some of the interior 

    // Use smoothstep(aa width, 0.0, distance) to smoothly blend onto the background
    color = mix(color, vec3(0.0, 1.0, 0.0), smoothstep(aaWidth, 0.0, box));
    color = mix(color, vec3(0.0), smoothstep(aaWidth, 0.0, abs(box) - 0.001)); // edges

    fragColor = vec4(color, 1.0);
}
