#define pi 3.14159265
#define time iTime*0.1
#define deg pi/180.

mat2 r2d(float a) {
    return mat2(cos(a),sin(a),-sin(a),cos(a));
}

float line(vec2 uv,vec2 s) {
    return smoothstep(max(abs(uv.x)-s.y,abs(uv.y)-s.x),0.,0.001);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 R = iResolution.xy;
    float ar = R.x/R.y;
    uv -= 0.5;
    uv.x *= ar;
    float c = length(uv);
    vec3 col = vec3(1.);
    float a = 1.-exp(-1.*time);
    a -= c*0.2;
    uv *= 0.4*(1.-exp(-0.3*time-0.1));
    for (int i=0;i<31;i++) {
        if (i > int(time*20.-log(c)*0.2)) {break;}
        col *= line(uv,vec2(0.01,0.01));
        uv = abs(uv);
        uv.x -= 0.02*a;
        uv.y -= 0.05*a;
        a *= 0.96;
        uv *= r2d(time/2.+sin(time*0.3)*0.6);
    }//uv *= r2d(col.r*0.1);
    //col *= 0.;
    col *= 0.4;
    col += 0.004;
    col += sin(col*vec3(0.1,0.2,0.3)*20.)*0.8;
    col += sin(col*vec3(0.5,0.2,0.45)*12./c*0.1)*0.4;
    col += sin(col*70.+c*60.-time*10.)*0.01;
    col += sin(col*100.+c*86.-time*5.)*0.003;
    //col += line(uv,vec2(0.01,0.01));
    //col += fract(col*2.-time)*2.;
    fragColor = vec4(col,1.0);
}
