vec3 Rand3D( vec3 pos ) // Less resource demanding to use the blue noise texture
{
	return textureLod( iChannel0, (pos.xy+pos.z)/256.0, 0.0 ).rgb;
}

void voronoi3D( in vec3 p, out vec3 coord, out float distToBorder, out float color)
{
    vec3 n = floor(p);
    vec3 f = fract(p);

    //----------------------------------
    // first pass: regular voronoi
    //----------------------------------
    distToBorder = 5.0;
    for( float x=-1.0; x<=1.0; x++ )
    for( float y=-1.0; y<=1.0; y++ )
    for( float z=-1.0; z<=1.0; z++ )
    {
        vec3 g = vec3(x,y,z);
		vec3 o = Rand3D( n + g );
        vec3 r = g + o - f;
        float d = length(r);

        if(d<distToBorder)
        {
            distToBorder = d;
            coord = r;
            color = o.x;
        } 
    }

    //----------------------------------
    // second pass: distance to borders
    //----------------------------------
    distToBorder = 8.0;
    for( float x=-1.0; x<=1.0; x++ )
    for( float y=-1.0; y<=1.0; y++ )
    for( float z=-1.0; z<=1.0; z++ )
    {
        vec3 g = vec3(x,y,z);
		vec3 o = Rand3D( n + g );
        vec3 r = g + o - f;
        vec3 normal = coord - r;

        if( dot(normal,normal)>0.001 ) {
            float j = dot( 0.5*(coord+r), normalize(r-coord));
            distToBorder = min( distToBorder, j );
        }
    }

    // coord is like the local coordinate of the voronoi
    // Adding p turns it into a universal coordinate
    // That's a poor way of explaining it, so comment the line to see what I mean
    coord += p;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv = (uv * 2.0 - 1.0)/2.0;
    uv.x *= iResolution.x/iResolution.y;
    uv *= 7.0;

    //the 3D model
    vec3 xyz = vec3(uv,iTime);
    vec3 coord;
    float distToBorder;
    float color;
    voronoi3D(xyz,coord,distToBorder,color);

    // The other outputs the 3D voronoi function returns
    //vec3 col = coord;
    //vec3 col = vec3(color);
    vec3 col = vec3(distToBorder);

    // Output to screen
    fragColor = vec4(col,1.0);
}
