const float fPi = 3.14159;

float GetStar(vec2 p, float fTime)
{
    float fLength = length(p);
    // fAngle 0.0 bis 1.0:
    float fAngle = (atan(p.y, p.x) * 0.5) / fPi + 0.5;


    float fWrinkle = fract(fAngle * 5.0 + fTime);

    return 0.6 * smoothstep(-0.3, 0.2, fWrinkle) * smoothstep(-2.0, 0.25, -fLength) ;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Koordinaten -1.0 bis 1.0 (x-Achse bei Breitbild entsprechend mehr)
    vec2 uv = fragCoord / iResolution.xy * 2.0 - vec2(1.0, 1.0);
    uv.x /= iResolution.y / iResolution.x;

    float fTime = iTime * 0.2;
    float fValue = 0.0;

    vec2 pos = vec2(0.0);
    float fi = 0.0;
    vec2 vDiss = vec2(0.0);

    for (int i = 0; i < 6; i++)
    {
        fi = float(i) * 0.3;
        vDiss = vec2(0.12 * cos(fTime * -fi * 0.7), 
                     0.32 * sin(fTime * fi * 1.3));


        pos = 0.3 * vec2(sin(fTime + fi + vDiss.x),
                         cos(fTime - fi + vDiss.y));
        fValue += GetStar(uv + pos, fTime);
    }


    float fColorSwitch = smoothstep(-0.3, 0.3, cos(fTime * 3.0 + fPi));
    //fValue *= fColorSwitch + 0.2;
    vec3 useColor = mix(vec3(0.2), vec3(0.9, 0.5, 0.25), fColorSwitch);


    vec3 color = fValue * useColor;

    // Pixel setzen
    fragColor = vec4(color,1.0);
}
