float rand(vec2 n) { 
return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

float noise(vec2 p){
vec2 ip = floor(p);
vec2 u = fract(p);
u = u*u*(3.0-2.0*u);
float res = mix(
    mix(rand(ip),rand(ip+vec2(1.0,0.0)),u.x),
    mix(rand(ip+vec2(0.0,1.0)),rand(ip+vec2(1.0,1.0)),u.x),u.y);
return res*res;
}


float wave(vec2 p, float i){
 float medium = noise((p + i * .77 ) * vec2(7. , .1)),
 small = noise((p + i * .33) * vec2(17.57 , .1)),
 large = noise((p + i * .56) * vec2(.052 , .5));
float offset = large - medium * .78 - small * .1;
float a = p.y + offset * .5;
a *= 1.5;
a -= .2;
float b = .7 - i * .05;
if(mod(i, 2.) > .5){
   a = 1. - a;
}
 return step(a,b);
}

vec3 colors[8] = vec3[]( 
    vec3( 1.0, 0.2, 0.0 ),
    vec3( 0.1, 0.2, 0.3 ),
    vec3( 0.1, 0.1, 0.0 ),
    vec3(0.137,0.137,0.122),
    vec3( 1.0, 1.0, 0.9 ),
    vec3( 0.4, 0.2, 1.0 ),
    vec3(0.953,0.325,0.514),
    vec3( 1.0, 0.1, 0.2 ));

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
vec2 uv = fragCoord/iResolution.y;
uv.x*=0.5;
vec2 u = uv;
float t = iTime * 0.2;
vec2 m = iMouse.xy/iResolution.xy;
u.x += m.x * 10.0;
vec3 color = colors[5];
for(float i = 1.0; i < 14.0; i++){
   float speed = noise(vec2(i*0.23,0.0));
   speed = i*0.1;
   vec2 v = u.xy;
   v.x += speed * t;
   float e = wave(v, i);
   color = mix(color, colors[int(mod(i-1., 8.0))],  e);
}
fragColor = vec4(color , 1.0);
}
