float Sun(vec2 uv, float r, float blur) {
    float d = length(uv);
    float c = smoothstep(r*0.8*(3.0+sin(iTime))*0.5, r*(0.5+cos(iTime*0.1)*0.3)-blur, d);
    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = 10.0 + iTime * 0.02;
    vec2 np = fragCoord / iResolution.y - .835;
    float w = length(np) * tanh(t) * (1.0+sin(t)*sin(t)*0.9)*1.20;        
    vec4 a = texture(iChannel0, vec2(atan(np.x, np.y*(1.0+sin(t))) * .004 / sin(np.x*0.2), .2 / w) + t) * w * (2.0+tanh(t)) * 0.1 * 2.0;            
    float s = Sun(np+vec2(0.0, 0.0), 0.5 + (1.0*0.05+sin(iTime)*0.05) * 0.5, .2);
    fragColor.r = a.r*a.r*a.r * 2.3 + s;
    fragColor.g = 0.7 -  a.g * 2.3 + s;
    fragColor.b = a.b * a.b * 2.9 + (a.b * 0.6 + s * 0.4) / 2.0;
}
