//----------------Mouse Drag-------------
vec2 MouseDragRead(){
    #define MouseTex iChannel1
    #define Scale 32767.
	uvec2 data = floatBitsToUint(GetValue(MouseTex,MOUSE_COORD).zw);
	vec4 MousePos = vec4(unpackSnorm2x16(data.x),unpackSnorm2x16(data.y))*Scale,tmpMousePos = vec4(MousePos.xy,0.,0.);
    iMouse.z>0. ? tmpMousePos.xy+=tmpMousePos.zw=iMouse.xy-iMouse.zw : tmpMousePos.xy=MousePos.xy+=MousePos.zw;
    return tmpMousePos.xy;
}
//---------------------------------------
vec3 IBL(vec3 dir) {return texture(iChannel0, dir).rgb;}
mat3 lookAt(vec3 pos, vec3 focus) {
    mat3 mat;
    mat[2] = normalize(focus - pos);			
    mat[0] = normalize(cross(mat[2], vec3(0, 1, 0)));
    mat[1] = normalize(cross(mat[0], mat[2]));
    return mat;
}
vec3 getAxis(vec3 _dir){
    vec3 dir = abs(_dir);
	//float min_a = min(dir.x,min(dir.y,dir.z));
    vec3 vec_xyz = dir - min(dir.x,min(dir.y,dir.z)); 
    vec3 vec_T_xyz = vec_xyz-(vec_xyz.x+vec_xyz.y+vec_xyz.z)*0.5;
    return sign(max(vec_T_xyz,0.)*_dir);//*sign();
}
vec3 GetUVW(vec3 axis,vec3 dir){
    return dir/dot(axis,dir)-axis;
}
vec3 GetReadMipMapUVW_Dir(vec3 _uvw,vec3 _axis){
    return normalize(
        ((_uvw+3.)*exp2(vec3(5.-10.,8.-10.,2.-10.))-1.)
        * (1.-abs(_axis)) 
        + _axis
    );
}
vec3 GetReadMipMapUVW_Dir2(vec3 _uvw,vec3 _axis,float ID){
    ivec3 xyz = ID_xyz[int(dot(abs(_axis.yz),vec2(1,2)))];
	vec3 uv = vec3(_uvw[xyz.x],_uvw[xyz.y],_uvw[xyz.z]);
    float group_ID = floor((ID-=1.)/3.);
    float scale = exp2(group_ID-10.);
    float Bound = 1.-exp2(-group_ID);
    uv.xy = scale*(clamp(uv.xy,-Bound,Bound)+SCACLE_COEF[int(mod(ID,3.))])-1.;
    //f(x) = inverse(inverse(f(x)))
    uv = vec3(uv[xyz.x],uv[xyz.y],uv[xyz.z]);
    return normalize(uv + _axis);
}

vec3 TrilinearSamplerIBL(vec3 dir,float roughness){
    vec3 axis = getAxis(dir);
   	vec3 uvw = GetUVW(axis,dir);
    float roughness_ID = roughness * ID_Range.x;
    float pre_ID  = floor(30.-roughness_ID);
    float next_ID = pre_ID - 1.;
    vec3 pre_dir  = GetReadMipMapUVW_Dir2(uvw,axis,pre_ID);
	vec3 next_dir = GetReadMipMapUVW_Dir2(uvw,axis,next_ID);
    vec3 preCol   = IBL(pre_dir);
    vec3 nextCol  = IBL(next_dir);
    return mix(preCol,nextCol,fract(roughness * ID_Range.x));
}
vec3 ApproximateSpecularIBL( vec3 F, float Roughness, float NoV,vec3 R_dir) {
	vec3 PrefilteredColor = TrilinearSamplerIBL( R_dir,Roughness );
    vec2 EnvBRDF = texture(iChannel1,vec2(NoV,Roughness)).xy;
	return PrefilteredColor*( F * EnvBRDF.x + EnvBRDF.y);
}
//-----------------RayMarch-------------------
float sphere(vec3 pos,float r){
	return length(pos)-r;
}
float cube(vec3 pos,vec3 size,float r){
	return length(max(abs(pos)-size,0.)) - r;
}
float map(vec3 pos){
    float d = min(1.,cube  (pos-vec3(0.,-0.5, 0.),vec3(3.5,0.05,3.5),0.01));
    return d;
}
vec3 normal(vec3 pos){
	const vec2 e = vec2(0.001,0.);
    return normalize(
    	vec3(
        	map(pos + e.xyy),
            map(pos + e.yxy),
            map(pos + e.yyx)
        )-map(pos)
    );
}
struct IntersectInfo{
	vec3 surface;
	vec3 normal;
    float distance;
};
IntersectInfo raymarch(vec3 pos,vec3 dir){
	IntersectInfo info;
    info.distance = 1.;
    float d = 0.;
    for(int i=0;i<64;i++){
		info.surface = pos+dir*info.distance;
        d = map(info.surface);
		info.distance += d;
        if(d < 0.02||info.distance>31.) break;
    }
    info.surface += d*dir;
	info.normal = normal(info.surface);
    return info;
}
//---------------------------------------------
struct LightInfo{
	vec3 pos;
    vec3 emit;
};
LightInfo[] lights = LightInfo[](
    LightInfo(vec3(40.),vec3(1.)*100.)
);
struct Material{
	vec3 albedo;
    vec3 diffuseColor;
    float metallic;
	float roughness;
}; 
//---------------------------------------------
vec3 PBRShading(Material mat,vec3 surface,float ao,vec3 N_dir,vec3 V_dir){
    vec3 F0 = mix(vec3(0.04),mat.albedo,mat.metallic);
    vec3 R_dir = reflect(-V_dir,N_dir);
    //rotation 180 sky box get better reflect
    R_dir.xy = rotate(R_dir.xy,(180.)/180.*PI);

    float NoV = max(0.,dot(N_dir,V_dir));
    vec3 lightCol = vec3(0);
    //Here just point light
    for(int i=0;i<lights.length();i++){
        LightInfo light = lights[i];
        vec3 L_Direction = light.pos - surface;
    	vec3 L_dir = normalize(L_Direction);
        vec3 H_dir = normalize(L_dir + V_dir);
        float HoV = max(0.,dot(H_dir,V_dir));
        float NoL = max(0.,dot(N_dir,L_dir));
        float atten = 1./dot(L_Direction,L_Direction);
        vec3 radiance = light.emit * atten;
        //Cook-Torrance BRDF Lighting
        float D = DistributionGGX(N_dir,H_dir,mat.roughness);
        float G = GeometrySmith(N_dir,V_dir,L_dir,mat.roughness,false);
        vec3  F = fresnelSchlick(HoV,F0);
        vec3 specular = (F*D*G)/max(4.*NoV*NoL, 0.001);

        vec3 K_LightSpeclular = F;
    	vec3 K_LightDiffuse = (1.-K_LightSpeclular) * (1.-mat.metallic);
		lightCol += (K_LightDiffuse*mat.albedo*I_PI + specular)*radiance*NoL;
    }

    vec3 F_IBL = fresnelSchlickRoughness(NoV,F0,mat.roughness);
    vec3 K_IBLSpecular = F_IBL;
    vec3 K_IBLDiffuse = (1. - K_IBLSpecular) * (1.-mat.metallic);

    vec3 IBL_diffuse = TrilinearSamplerIBL(N_dir,0.7)*mat.diffuseColor;
    vec3 IBL_specular = ApproximateSpecularIBL(F_IBL,mat.roughness,NoV,R_dir);
    vec3 IBL_Col = (K_IBLDiffuse*IBL_diffuse + IBL_specular);

    vec3 col = (lightCol + IBL_Col) * ao;
    return col;
}

//--------------Struct Buffer Texture----------
struct StructBuffer{
    vec3 imgA;
    vec3 imgB;
    vec3 imgC;
    vec3 imgD;
    mediump vec2 other;
};
StructBuffer GetStructBuffer(vec4 data){
	StructBuffer Buffer;
    uvec4 stream = floatBitsToUint(data);
    vec4 imgAllData[3];
    for(int i=0;i<3;i++){
    	imgAllData[i] = vec4((uvec4(stream[i])>>ivec4(24,16,8,0))&0xFFu)/255.;
    }
    mat4x3 imgStream = mat4x3(imgAllData[0],imgAllData[1],imgAllData[2]);
    Buffer.imgA = imgStream[0];
    Buffer.imgB = imgStream[1];
    Buffer.imgC = imgStream[2];
    Buffer.imgD = imgStream[3];
    Buffer.other = unpackHalf2x16(stream.w);
	return Buffer;
}
StructBuffer TexturesSampler(sampler2D _buffer,vec2 uv){
	return GetStructBuffer(texture(_buffer,uv));
}

void mainImage(out vec4 C, in vec2 U) {
    vec2 mouse = (MouseDragRead()/R)*_2PI;
    vec2 uv = U/R;
    vec3 pos = vec3(6.*cos(mouse.x), 8.*sin(mouse.y*0.4), 6.*sin(mouse.x));
    vec3 dir = lookAt(pos, vec3(0)) * normalize(vec3((U+U-R)/R.y, 2.5));
	IntersectInfo march = raymarch(pos,dir);
    if(march.distance < 30.){
        vec2 texCoord = (march.surface.xz+3.5)/7.; 
        StructBuffer texs = TexturesSampler(iChannel2,texCoord);
        vec3 tex = texs.imgA;
        vec3 normal = ToTangentSpace(march.normal,texs.imgB*2.-1.);
        Material mat;
        mat.albedo = vec3(1.);
        mat.diffuseColor = mix(tex,vec3(1.),(sin(iTime)+1.)*0.5);
        mat.metallic = clamp(1.-texs.imgC.g-.2,0.,1.);
        mat.roughness = clamp(mix(tex.r,texs.imgD.r,(cos(iTime*0.2)+1.)*0.5),0.,0.6)*min(1.,length(march.surface.xz/2.));
        C.rgb = PBRShading(mat,march.surface,1.,normal,-dir);
    }
    else {
        dir.xy = rotate(dir.xy,(iTime)/50.*PI);
        C.rgb = TrilinearSamplerIBL(dir,0.2)*0.15;
    }
    C.rgb = HDR(C.rgb,2.2);
    C.rgb = pow(C.rgb,vec3(1./2.2));
    //C.rgb = texture(iChannel0,dir).rgb;//vec3(U-MouseDragWriteRead(),0.);
    //C.rgb = TexturesSampler(iChannel2,uv).imgB;
	//C.rgb = texture(iChannel1,uv).rgb;
    //C.rgb = TrilinearSamplerIBL(dir,0.15);
}

#define R iResolution.xy
const vec3 ID_Range = vec3(16,30,14);//start,end,length
const ivec3[3] ID_xyz = ivec3[](ivec3(2,1,0),ivec3(0,2,1),ivec3(0,1,2));
const vec2[3] SCACLE_COEF = vec2[](vec2(3,1),vec2(1,3),vec2(3,3));
const ivec2 RESIZE_COORD= ivec2(0,0);
const ivec2 MOUSE_COORD = ivec2(1,1);
vec4 GetValue(sampler2D _tex,ivec2 coord){return texelFetch(_tex,coord,0);}
bool IsCoord(ivec2 iU,ivec2 coord){return all(equal(iU,coord));}

//--------------Simple Math----------------
#define    PI 3.1415926
#define  _2PI 6.2831853
#define  I_PI 0.3183098 /* 1/pi    */
#define I_2PI 0.1591549 /* 1/(2pi) */

float POW5(float a){
	float b = a * a;
    return b*b*a;
}
//----------------------------------------------
vec2 rotate(vec2 dir, float angle){
    vec2 rot;
    rot.x = dir.x*cos(angle) - dir.y*sin(angle);
    rot.y = dir.x*sin(angle) + dir.y*cos(angle);
    return rot;
}
void frisvad(in vec3 n, out vec3 f, out vec3 r){
    if(n.z < -0.999999) {
        f = vec3(0.,-1,0);
        r = vec3(-1, 0, 0);
    }
    else{
    	float a = 1./(1.+n.z);
    	float b = -n.x*n.y*a;
    	f = vec3(1. - n.x*n.x*a, b, -n.x);
    	r = vec3(b, 1. - n.y*n.y*a , -n.y);
    }
}
mat3 CoordBase(vec3 n){
	vec3 x,y;
    frisvad(n,x,y);
    return mat3(x,y,n);
}
vec3 ToOtherSpaceCoord(mat3 otherSpaceCoord,vec3 vector){
	return vector * otherSpaceCoord;
}
vec3 ToTangentSpace(vec3 normal,vec3 vector){
	return ToOtherSpaceCoord(CoordBase(normal),vector);
}
